mapper.select:
WITH RHDL_INFO AS ( SELECT /* rehandlingOfRORO.selectOriginalCargoItems */ VSL_CALL_ID, MF_DOC_ID, CG_NO, IX_CD, SUM (ISNULL(CG_WGT, DOC_WGT)) AS RHDL_WGT, COUNT(1) AS RHDL_QTY, SUM(CBM) AS RHDL_M3 FROM TMT_RORO_MST WHERE (RHDL_NO IS NOT NULL AND RHDL_NO <> '') AND STAT_CD = 'RH'
AND VSL_CALL_ID = #{vslCallId}
GROUP BY VSL_CALL_ID, MF_DOC_ID, CG_NO, IX_CD ),
RORO AS (SELECT DISTINCT /* rehandlingOfRORO.selectOriginalCargoItems */ R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO, R.IX_CD, R.RHDL_NO, R.RHDL_MODE, MIN(IN_DTM) OVER (PARTITION BY R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO) AS MIN_IN_DTM, MAX(IN_DTM) OVER (PARTITION BY R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO) AS MAX_IN_DTM, R.WH_ID AS WHLOC FROM TMT_RORO_MST R WHERE (IN_DTM IS NOT NULL AND IN_DTM <> '') AND (((OUT_DTM IS NULL OR OUT_DTM = '') AND (RHDL_NO IS NULL OR RHDL_NO = '')) OR ((RHDL_NO IS NOT NULL AND RHDL_NO <> '') AND STAT_CD = 'RH'))
AND R.VSL_CALL_ID = #{vslCallId}
), RORO_STORED AS ( SELECT VSL_CALL_ID, CG_NO, COUNT(CHASSIS_NO) AS PKG_QTY, SUM(DOC_WGT) AS CG_WGT, SUM(CBM) AS CG_VOL FROM TMT_RORO_MST WHERE STAT_CD = 'ST'
AND VSL_CALL_ID = #{vslCallId}
AND (RHDL_MODE IS NULL OR RHDL_MODE = '') AND (RHDL_NO IS NULL OR RHDL_NO = '') GROUP BY VSL_CALL_ID, CG_NO )
UNION ALL

WITH RHDL_INFO AS ( SELECT VSL_CALL_ID, MF_DOC_ID, CG_NO, IX_CD, SUM (CG_WGT) AS RHDL_WGT, COUNT(1) AS RHDL_QTY, SUM(CBM) AS RHDL_M3 FROM TMT_RORO_MST WHERE (RHDL_NO IS NOT NULL AND RHDL_NO <> '') AND STAT_CD = 'RH'
AND VSL_CALL_ID = #{vslCallId}
GROUP BY VSL_CALL_ID, MF_DOC_ID, CG_NO, IX_CD ),
RORO AS ( SELECT DISTINCT R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO, R.IX_CD, R.RHDL_NO, R.RHDL_MODE, MIN(IN_DTM) OVER (PARTITION BY R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO) AS MIN_IN_DTM, MAX(IN_DTM) OVER (PARTITION BY R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO) AS MAX_IN_DTM, '' AS WHLOC FROM TMT_RORO_MST R WHERE IN_DTM IS NOT NULL AND ( ( (OUT_DTM IS NULL OR OUT_DTM = '') AND (RHDL_NO IS NULL OR RHDL_NO = '') ) OR ( (RHDL_NO IS NOT NULL AND RHDL_NO <> '') AND STAT_CD = 'RH' ) )
AND R.VSL_CALL_ID = #{vslCallId}
),
RORO_STORED AS ( SELECT VSL_CALL_ID, CG_NO, COUNT(CHASSIS_NO) AS PKG_QTY, SUM(DOC_WGT) AS CG_WGT, SUM(CBM) AS CG_VOL FROM TMT_RORO_MST WHERE STAT_CD = 'ST'
AND VSL_CALL_ID = #{vslCallId}
AND (RHDL_MODE IS NULL OR RHDL_MODE = '') AND (RHDL_NO IS NULL OR RHDL_NO = '') GROUP BY VSL_CALL_ID, CG_NO )
SELECT /* rehandlingOfRORO.selectOriginalCargoItemsCount */ COUNT(*) FROM (
UNION ALL
) AS selectOriginalCargoItemsCount

SELECT /* rehandlingOfRORO.selectStackedUnitItems */ R.VSL_CD, R.CALL_SEQ, R.CALL_YEAR, R.VSL_CALL_ID, R.MF_DOC_ID, R.CG_NO, R.CHASSIS_NO AS CHAS_NO, R.RORO_SEQ, R.BRAND_CD, B.BRAND_NM, R.MODEL_CD, M.MODEL_NM, ISNULL(R.CATG_CD, R.IX_CD) AS CATG_CD, dbo.F_CM_CODE_NM( 'MT', 'CATGTP', ISNULL(R.CATG_CD, R.IX_CD) ) AS CATG_NM, R.DOC_WGT AS CG_WGT, R.CBM, R.IX_CD, R.LOC_ID FROM TMT_RORO_MST R LEFT OUTER JOIN TMT_BRAND B ON R.BRAND_CD = B.BRAND_CD LEFT OUTER JOIN TMT_BRAND_DTL M ON R.MODEL_CD = M.MODEL_CD AND R.BRAND_CD = M.BRAND_CD WHERE (RHDL_NO IS NULL OR RHDL_NO = '') AND (IN_DTM IS NOT NULL AND IN_DTM <> '') AND (OUT_DTM IS NULL OR OUT_DTM = '')
AND VSL_CALL_ID = #{vslCallId}
AND MF_DOC_ID = #{docNo}
AND CG_NO = #{cargoNo}
AND IX_CD = #{ixCd}
AND CHASSIS_NO = #{unitNo}
AND R.STAT_CD = 'ST'

SELECT /* rehandlingOfRORO.selectRehandlingUnitItems */ DISTINCT R.CHASSIS_NO AS CHAS_NO, R.RORO_SEQ, R.BRAND_CD, B.BRAND_NM, R.MODEL_CD, M.MODEL_NM, R.CATG_CD, dbo.F_CM_CODE_NM('MT', 'CATGTP', R.CATG_CD) AS CATG_NM, R.CG_WGT, R.CBM, H.VSL_CALL_ID, H.NX_VSL_CALL_ID, H.ORG_REF_NO, H.NX_REF_NO, R.RHDL_NO, R.RHDL_MODE FROM TMT_RORO_MST R INNER JOIN TMT_RHDL_CG H ON R.RHDL_MODE = H.RHDL_MODE AND R.RHDL_NO = H.RHDL_NO LEFT OUTER JOIN TMT_BRAND B ON R.BRAND_CD = B.BRAND_CD LEFT OUTER JOIN TMT_BRAND_DTL M ON R.MODEL_CD = M.MODEL_CD AND R.BRAND_CD = M.BRAND_CD WHERE (R.RHDL_NO IS NOT NULL AND R.RHDL_NO <> '')
AND STAT_CD != 'RH'
