mapper.insert:
INSERT INTO TMT_RHDL_CG_DTL( RHDL_NO, SEQ, PKG_QTY, CG_WGT, CG_VOL, STAFF_CD, UPDATE_TIME, VERSION ) VALUES ( #{rhdlNo}, (SELECT CASE WHEN MAX(SEQ) IS NULL THEN 1 ELSE MAX(SEQ) + 1 END FROM TMT_RHDL_CG_DTL WHERE RHDL_NO = #{rhdlNo} ), #{rhdlPkgQty}, #{rhdlWgt}, #{rhdlMsrmt}, #{userId}, SYSDATETIME(), #{newVersion} );
MERGE INTO TMT_RHDL_CG A USING (SELECT '00000' AS COSTCD) B ON ( A.RHDL_NO = #{rhdlNo} AND B.COSTCD = '00000' ) WHEN MATCHED THEN UPDATE SET PKG_QTY = PKG_QTY + #{rhdlPkgQty}, CG_WGT = CG_WGT + #{rhdlWgt}, CG_VOL = CG_VOL + #{rhdlMsrmt}, A.UPDATE_TIME = SYSDATETIME() WHEN NOT MATCHED THEN INSERT ( RHDL_NO, VSL_CALL_ID, ORG_REF_NO, NX_VSL_CALL_ID, NX_REF_NO, OPE_CLASS_CD, PKG_QTY, CG_WGT, CG_VOL, RHDL_MODE, CG_NO, UPDATE_TIME, STAFF_CD, NX_CG_NO, RHDL_GROUP_NO, ORG_MF_DOC_ID, NX_MF_DOC_ID ) VALUES ( #{rhdlNo}, #{vslCallId}, #{orgRefNo}, #{nextVslCallId}, #{nextRefNo}, #{opeClassCd}, #{rhdlPkgQty}, #{rhdlWgt}, #{rhdlMsrmt}, #{rhdlModeCd}, #{cargoNo}, SYSDATETIME(), #{userId}, #{cargoNo}, #{rhdlGroupNo}, #{docNo}, #{docNo} );
MERGE INTO TMT_SHIPG_NOTE S USING TMT_RHDL_CG C ON ( S.SHIPG_NOTE_NO = #{nextRefNo} AND S.VSL_CALL_ID = #{nextVslCallId} AND S.SHIPG_NOTE_NO = C.NX_REF_NO AND S.VSL_CALL_ID = C.NX_VSL_CALL_ID ) WHEN MATCHED THEN UPDATE SET S.PKG_QTY = C.PKG_QTY, S.CG_WGT = C.CG_WGT, S.CG_VOL = C.CG_VOL, S.UPDATE_TIME = SYSDATETIME();

