SELECT /* mega.selectMegaEquipment */ A.MEGA_NO AS MEGANO, A.SEQ AS SEQ, A.DIV_CD AS DIVCD, A.EQ_DIV_CD AS EQDIVCD, dbo.F_CM_CODE_NM('MT', 'EQFCTPCD', A.EQ_DIV_CD) AS EQDIVCDNM, ISNULL(A.CAPA_CD, '') AS CAPACD, CASE A.EQ_DIV_CD WHEN 'SR' THEN '-' ELSE (SELECT CAPA_DESCR FROM TMT_EQ_CAPA WHERE CAPA_CD = A.CAPA_CD
AND EQ_TP_CD = A.EQ_DIV_CD
) END AS CAPADESCR, CASE WHEN (A.REQ_HHMM IS NOT NULL AND A.REQ_HHMM <> '') THEN SUBSTRING(A.REQ_HHMM, 1, 2) ELSE 0 END AS REQTIME, CASE WHEN (A.REQ_HHMM IS NOT NULL AND A.REQ_HHMM <> '') THEN SUBSTRING(A.REQ_HHMM, 3, 2) ELSE 0 END AS REQMIN, LEFT(A.REQ_HHMM, 2) + RIGHT(A.REQ_HHMM, 2) AS DSPREQHHMM, CASE WHEN (A.REQ_QTY IS NULL) THEN 0 ELSE A.REQ_QTY END AS REQQTY,
FORMAT(START_DATE, 'dd/MM/yyyy HH:mm') AS WORKSTDT, FORMAT(END_DATE, 'dd/MM/yyyy HH:mm') AS WORKENDDT, FL_STATUS AS FLSTATUS,
0 AS CONFMQTY, 0 AS WHQTY,
CASE A.CONFM_QTY WHEN 0 THEN 0 ELSE A.CONFM_QTY END AS CONFMQTY, CASE A.WH_QTY WHEN 0 THEN 0 ELSE A.WH_QTY END WHQTY,
ISNULL(A.LOC_ID, ' ') AS LOCID, ' ' AS COLCOLOR, #{copyType, jdbcType=VARCHAR} AS COPYTYPE FROM TMT_MEGA_DTL A WHERE A.MEGA_NO = #{megaNo} AND A.DIV_CD = #{divCd}
AND A.CAPA_CD IN (SELECT B.CAPA_CD FROM TMT_EQ_CAPA B, TMT_CD_MSTD C WHERE B.EQ_TP_CD = C.S_CD AND C.M_CD = 'EQFCTPCD' AND C.S_CD_LGV = 'EQ' AND C.S_CD_VAL = 'GR' GROUP BY B.EQ_TP_CD, C.S_CD_NM, B.EQ_TP_CD, B.CAPA_CD, B.CAPA_DESCR, B.CAPA)
AND A.EQ_DIV_CD = #{eqDivCdType}
AND A.EQ_DIV_CD = #{eqDivCdType}
AND EQ_DIV_CD NOT IN ('CH', 'CY', 'EH', 'EX', 'FN', 'GR', 'PIP', 'RP', 'RS', 'WB', 'YT', 'FL', 'TR', 'TC', 'QC', 'SC', 'HG', 'RT', 'SR', 'CC', 'CU', 'LL', 'PC', 'FC') AND dbo.F_CM_CODE_NM('MT', 'EQFCTPCD', A.EQ_DIV_CD) NOT LIKE '%CRANE%'
AND dbo.F_CM_CODE_NM('MT', 'EQFCTPCD', A.EQ_DIV_CD) LIKE '%CRANE%'
ORDER BY SEQ ASC

SELECT /* mega.selectMegaEquipmentForReport */ ROW_NUMBER() OVER(ORDER BY MEGANO, WORKYMD, SHFTNM) AS NO, * FROM (SELECT A.MEGA_NO AS MEGANO, A.SEQ SEQ, CASE WHEN A.req_qty IS NULL OR A.req_qty = '' THEN 0 ELSE A.req_qty END AS reqqty, CASE WHEN A.confm_qty = NULL THEN 0 ELSE A.confm_qty END AS confm_qty, A.DIV_CD AS DIVCD, A.EQ_DIV_CD AS EQDIVCD, dbo.F_CM_CODE_NM('MT', 'EQFCTPCD', A.EQ_DIV_CD) AS EQDIVCDNM, ISNULL(A.CAPA_CD, ' ') AS CAPACD, CASE A.EQ_DIV_CD WHEN 'SR' THEN '-' ELSE (SELECT CAPA_DESCR FROM TMT_EQ_CAPA WHERE CAPA_CD = A.CAPA_CD AND EQ_TP_CD = A.EQ_DIV_CD) END AS CAPADESCR, CASE WHEN (A.REQ_HHMM IS NOT NULL AND A.REQ_HHMM <> '') THEN SUBSTRING(A.REQ_HHMM, 1, 2) ELSE 0 END AS REQTIME, CASE WHEN (A.REQ_HHMM IS NOT NULL AND A.REQ_HHMM <> '') THEN SUBSTRING(A.REQ_HHMM, 3, 2) ELSE 0 END AS REQMIN, LEFT(A.REQ_HHMM, 2) + RIGHT(A.REQ_HHMM, 2) AS DSPREQHHMM,
' ' AS CONFMQTY, ' ' AS WHQTY,
CASE WHEN (A.CONFM_QTY IS NOT NULL AND A.CONFM_QTY <> '') THEN ' ' ELSE A.CONFM_QTY END AS REQQTY, ISNULL(A.WH_QTY, 0) AS WHQTY,
ISNULL(A.LOC_ID, ' ') AS LOCID, ' ' AS COLCOLOR, #{copyType} AS COPYTYPE, B.SHFT_ID AS SHFTID, B.SUMIT_BY AS submitBy, (SELECT SHFT_NM FROM TMT_SHFT WHERE SHFT_ID = B.SHFT_ID) AS SHFTNM, (SELECT SHFT_IDX FROM TMT_SHFT WHERE SHFT_ID = B.SHFT_ID) AS SHFTIDX, B.PURP_TP_CD AS PURPTPCD, B.RMK AS rmk, B.REJ_RMK AS rejRmk, B.APPRV_BY AS apprvBy, B.STAT_CD AS statCd, dbo.F_CM_CODE_NM('MT', 'MGPURP', B.PURP_TP_CD) AS PURPTPCDNM, FORMAT( CONVERT(DATE, B.WORK_YMD , 112), 'dd/MM/yyyy' ) AS WORKYMD, dbo.F_CM_CODE_NM('MT', 'MEGASTAT', B.STAT_CD) AS STATCDNM, (SELECT S_CD_LGV FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'MGPURP' AND S_CD = B.PURP_TP_CD) AS PURPTYPE, '' AS BERTHLOC FROM TMT_MEGA_DTL A, TMT_MEGA B WHERE A.MEGA_NO = B.MEGA_NO AND B.VSL_CALL_ID = #{vslCallId} AND A.DIV_CD = #{divCd}
AND CONVERT(DATETIME, B.WORK_YMD,'YYYYMMDD') = CONVERT(DATETIME, #{etaFrom},'DD/MM/YYYY')
AND A.EQ_DIV_CD = #{eqDivCdType}
AND A.EQ_DIV_CD = #{eqDivCdType}
AND A.EQ_DIV_CD = #{eqDivCdType}
AND EQ_DIV_CD NOT IN ('GR', 'FL', 'TR','BG','CU','LL','PC','TX','SR')
AND (A.EQ_DIV_CD = 'SR' OR EXISTS ( SELECT S_CD FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'EQFCTPCD' AND S_CD = A.EQ_DIV_CD AND S_CD_VAL = 'PC' ) )
AND B.REQR = (SELECT TOP(1) C.PTNR_CODE FROM TMT_PTNR_USER C WHERE C.USER_ID = #{userId})
AND A.MEGA_NO IN
${item}
) AS TEMPTB

