SELECT /* mega.selectConfirmationSlipDryBreakBulk */ A.VSL_CALL_ID AS VSLCALLID, B.SEQ AS SEQ, B.CG_TP_CD AS CGTPCD, ISNULL(B.OPE_TP_CD,' ') AS OPETPCD, (SELECT S_CD_NM FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'CGTP' AND S_CD = B.CG_TP_CD) AS CGTPNM, ISNULL(B.CMDT_CD,' ') AS CMDTCD, ISNULL( (SELECT DISTINCT D.CMDT_GRP_CD FROM TMT_CMDT D WHERE D.CMDT_CD = B.CMDT_CD) , ' ') AS CMDTGRCD, ISNULL( (SELECT DISTINCT D.CMDT_GRP_DESC FROM TMT_CMDT D WHERE D.CMDT_CD = B.CMDT_CD) , ' ') AS CMDTGR, ISNULL(B.PKG_TP_CD,' ') AS PKGTPCD, ISNULL(B.OPE_HR,0) AS OPEHR, ISNULL(B.WORK_DD,0) AS WORKDD, ISNULL(B.WORK_HATCH_NO,' ') AS WORKHATCHNO, ISNULL(B.QTY,0) AS QTY, B.CLN_CD AS CLNCD, B.TOP_CG_CD AS TOPCGCD, (CASE WHEN B.CLN_CD = 'Y' THEN 'CLEAN' WHEN B.TOP_CG_CD ='Y' THEN 'TOP' ELSE '' END ) AS TOPCLN, B.TMNL_OPR AS TMNLOPR, B.SHPR_CNSNE AS SHPRCNSNE, B.CNSNE AS CNSNE, B.UNNO AS UNNO, B.IMDG AS IMDG, B.POL AS POL, B.FDEST AS FDEST, ISNULL(B.CRC,0) AS CRC, ISNULL(B.BL_NO,' ') AS BLNO, B.CG_OPT_TP_CD AS CGOPTTPCD, dbo.F_CM_CODE_NM('MT', 'CGOPETP', B.CG_OPT_TP_CD) AS CGOPTTPNM, B.DG_SEQ AS DGSEQ, (CASE WHEN C.SEQ IS NULL THEN 'N' ELSE 'Y' END) AS DGCHK, OPE_CATEGORY AS opeType, B.RMK AS remark, dbo.F_GET_PARTNER_INFO(B.CNSNE, 'ENG_SNM') AS CNSNENM, dbo.F_GET_PARTNER_INFO(B.CNSNE, 'ADDR') AS CNSNEADDR, dbo.F_GET_PARTNER_INFO(B.SHPR_CNSNE, 'ENG_SNM') AS SHPNM, dbo.F_GET_PARTNER_INFO(B.SHPR_CNSNE, 'ADDR') AS SHPADDR, dbo.F_CM_CODE_NM('MT', 'PKGTP', B.PKG_TP_CD) AS PKGTPNM FROM TMT_CONFM_SLP A, TMT_CONFM_SLP_DTL B LEFT OUTER JOIN TMT_DG C ON B.DG_SEQ = C.SEQ WHERE A.VSL_CALL_ID = B.VSL_CALL_ID AND A.VSL_CALL_ID = #{vslCallId}
AND B.CG_TP_CD IN ('BBK','DBE','DBN', 'CTR')
AND B.CG_TP_CD IN ('LQE','LQN')
