mapper.sql:
DECODE( (SELECT COUNT(*) FROM TMT_DEPY E, TMT_STAFF F WHERE E.USE_YN = 'Y' AND E.RS_DIV_CD = 'MP'
AND E.PURP_TP_CD = #{purpTpCd}
AND E.VSL_CALL_ID like '%STRG%'
AND E.VSL_CALL_ID = #{vslCallId}
AND E.SHFT_ID = #{shftId} AND E.WORK_YMD = TO_CHAR(TO_DATE(#{workYmd}, 'DD/MM/YYYY'), 'YYYYMMDD') AND E.EMP_ID = F.EMP_ID AND F.USE_YN = 'Y' AND F.EMP_ID = A.EMP_ID), 0, DECODE( (SELECT COUNT(*) FROM TMT_DEPY E, TMT_STAFF F WHERE E.USE_YN = 'Y' AND E.RS_DIV_CD = 'MP' AND E.SHFT_ID = #{shftId} AND E.WORK_YMD = TO_CHAR(TO_DATE(#{workYmd}, 'DD/MM/YYYY'), 'YYYYMMDD') AND E.EMP_ID = F.EMP_ID AND F.USE_YN = 'Y' AND F.EMP_ID = A.EMP_ID), 0, ' ', #{colColor2, jdbcType=VARCHAR} ), #{colColor1, jdbcType=VARCHAR} ) COLCOLOR

WHERE A.STAT_CD = 'AP'
AND (A.MEGA_TP_CD IS NULL OR A.MEGA_TP_CD <> 'H')

	AND A.PURP_TP_CD = #{purpTpCd}


	AND	A.VSL_CALL_ID <> 'STRG'


	AND	A.VSL_CALL_ID = #{vslCallId}

AND A.SHFT_ID = #{shftId}
AND A.WORK_YMD = TO_CHAR(TO_DATE(#{workYmd}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND A.MEGA_NO = B.MEGA_NO

SELECT A.WORK_YMD AS SVCDATE, TO_CHAR(TO_DATE(A.WORK_YMD, 'YYYYMMDD'), 'DD/MM/YYYY') AS WORKYMD, A.VSL_CALL_ID AS VSLCALLID, (SELECT S_CD_LGV FROM TMT_CD_MSTD WHERE L_CD='MT' AND M_CD='MGPURP' AND S_CD=A.PURP_TP_CD) AS PURPTYPE, A.PURP_TP_CD AS PURPTPCD, F_CM_012('MT', 'MGPURP', A.PURP_TP_CD) AS PURPTPCDNM, A.SHFT_ID AS SHFTID, (SELECT SHFT_NM FROM TMT_SHFT WHERE SHFT_ID = A.SHFT_ID) AS SHFTNM, (SELECT SHFT_IDX FROM TMT_SHFT WHERE SHFT_ID = A.SHFT_ID) AS SHFTIDX, C.VSL_NM AS VSLNM, ( SELECT WH_UPD_YN FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND WORK_YMD = A.WORK_YMD AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND WH_UPD_YN = 'Y' AND ROWNUM = 1 ) AS WHUPDYN, TO_CHAR(B.ETA, 'DD/MM/YYYY HH24:MI') AS ETA, TO_CHAR(B.ETB, 'DD/MM/YYYY HH24:MI') AS ETB, TO_CHAR(B.ATD, 'DD/MM/YYYY HH24:MI') AS ATD, B.ARRV_SA_ID AS SAID, NVL( (SELECT D.NX_BERTH_NO FROM TMT_VSL_SHFT D WHERE B.VSL_CALL_ID = D.VSL_CALL_ID AND D.SEQ = ( SELECT MAX(E.SEQ) FROM TMT_VSL_SHFT E WHERE D.VSL_CALL_ID = E.VSL_CALL_ID) ), B.BERTH_LOC ) AS BERTHLOC, ( SELECT 'Y' FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND WORK_YMD = A.WORK_YMD AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND RS_DIV_CD = 'MP' AND ROWNUM = 1 ) AS MANPOWERYN, ( SELECT 'Y' FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND WORK_YMD = A.WORK_YMD AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND RS_DIV_CD = 'EQ' AND EQ_TP_CD NOT IN ('FL') AND EQ_TP_CD IS NOT NULL AND ROWNUM = 1 ) AS PORTCRNYN, ( SELECT 'Y' FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND WORK_YMD = A.WORK_YMD AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND RS_DIV_CD = 'EQ' AND EQ_TP_CD IN ('FL') AND ROWNUM = 1 ) AS FORKLIFTYN, ( SELECT TO_CHAR(MAX(UPDATE_TIME),'DD/MM/YYYY HH24:MI:SS') FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND WORK_YMD = A.WORK_YMD ) AS UPDDT, ( SELECT D.STAFF_CD || '/' || E.ENG_NM FROM TMT_DEPY D, TMT_USER_INFO E WHERE D.USE_YN = 'Y' AND D.VSL_CALL_ID = A.VSL_CALL_ID AND D.PURP_TP_CD = A.PURP_TP_CD AND D.SHFT_ID = A.SHFT_ID AND D.WORK_YMD = A.WORK_YMD AND D.UPDATE_TIME = ( SELECT MAX(UPDATE_TIME) FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND WORK_YMD = A.WORK_YMD ) AND D.STAFF_CD = E.USER_ID AND ROWNUM = 1 ) AS UPDUSERID FROM ( SELECT WORK_YMD, VSL_CALL_ID, PURP_TP_CD, SHFT_ID FROM TMT_DEPY WHERE USE_YN = 'Y'
AND VSL_CALL_ID = #{vslCallId}
AND TO_DATE(WORK_YMD, 'YYYYMMDD') BETWEEN TO_DATE(#{etaFrom},'DD/MM/YYYY') AND TO_DATE(#{etaTo},'DD/MM/YYYY') AND SUBSTR(VSL_CALL_ID,3,9) <> 'STRG'
AND PURP_TP_CD = #{purpTpCd}
AND SHFT_ID = #{shftId}
GROUP BY WORK_YMD, VSL_CALL_ID, PURP_TP_CD, SHFT_ID ) A, TMT_VSL_SCH B, TMT_VSL_PART C WHERE A.VSL_CALL_ID = B.VSL_CALL_ID AND B.VSL_CD = C.VSL_CD
AND A.VSL_CALL_ID = #{vslCallId}
AND TO_DATE(A.WORK_YMD, 'YYYYMMDD') BETWEEN TO_DATE(#{etaFrom},'DD/MM/YYYY') AND TO_DATE(#{etaTo},'DD/MM/YYYY')
AND A.PURP_TP_CD = #{purpTpCd}
AND A.SHFT_ID = #{shftId}
ORDER BY SVCDATE DESC, SHFTIDX DESC, PURPTPCD

SELECT /* VOperationDeployMap.xml selectNonVslDeployList PN118LST */ A.WORK_YMD SVCDATE, TO_CHAR(TO_DATE(A.WORK_YMD , 'YYYYMMDD'), 'DD/MM/YYYY') WORKYMD, A.VSL_CALL_ID VSLCALLID, (SELECT S_CD_LGV FROM TMT_CD_MSTD WHERE L_CD='MT' AND M_CD='MGPURP' AND S_CD=A.PURP_TP_CD) PURPTYPE, A.PURP_TP_CD PURPTPCD, F_CM_012('MT', 'MGPURP', A.PURP_TP_CD) PURPTPCDNM, A.SHFT_ID SHFTID, (SELECT SHFT_NM FROM TMT_SHFT WHERE SHFT_ID = A.SHFT_ID) SHFTNM, (SELECT SHFT_IDX FROM TMT_SHFT WHERE SHFT_ID = A.SHFT_ID) SHFTIDX, ( SELECT WH_UPD_YN FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND WORK_YMD = A.WORK_YMD AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND WH_UPD_YN = 'Y' AND ROWNUM = 1 ) WHUPDYN, ( SELECT 'Y' FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND WORK_YMD = A.WORK_YMD AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND RS_DIV_CD = 'EQ' AND EQ_TP_CD IN ('FL') AND ROWNUM = 1 ) FORKLIFTYN, ( SELECT TO_CHAR(MAX(UPDATE_TIME),'DD/MM/YYYY HH24:MI:SS') FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND WORK_YMD = A.WORK_YMD ) UPDDT, ( SELECT D.STAFF_CD || '/' || E.ENG_NM FROM TMT_DEPY D, TMT_USER_INFO E WHERE D.USE_YN = 'Y' AND D.VSL_CALL_ID = A.VSL_CALL_ID AND D.PURP_TP_CD = A.PURP_TP_CD AND D.SHFT_ID = A.SHFT_ID AND D.WORK_YMD = A.WORK_YMD AND D.UPDATE_TIME = ( SELECT MAX(UPDATE_TIME) FROM TMT_DEPY WHERE USE_YN = 'Y' AND VSL_CALL_ID = A.VSL_CALL_ID AND PURP_TP_CD = A.PURP_TP_CD AND SHFT_ID = A.SHFT_ID AND WORK_YMD = A.WORK_YMD ) AND D.STAFF_CD = E.USER_ID AND ROWNUM = 1 ) UPDUSERID FROM ( SELECT WORK_YMD, VSL_CALL_ID, PURP_TP_CD, SHFT_ID FROM TMT_DEPY WHERE USE_YN = 'Y' AND SUBSTR(VSL_CALL_ID,3,9) = 'STRG' GROUP BY WORK_YMD, VSL_CALL_ID, PURP_TP_CD, SHFT_ID ) A WHERE TO_DATE(A.WORK_YMD,'YYYYMMDD') BETWEEN TO_DATE(#{etaFrom},'DD/MM/YYYY') AND TO_DATE(#{etaTo},'DD/MM/YYYY')
AND A.PURP_TP_CD = #{purpTpCd}
AND A.SHFT_ID = #{shftId}
ORDER BY SVCDATE DESC, SHFTIDX DESC, PURPTPCD

AND A.WORK_YMD = TO_CHAR(TO_DATE(#{workYmd}, 'DD/MM/YYYY'), 'YYYYMMDD')

	AND	A.PURP_TP_CD = #{purpTpCd}


	AND	A.VSL_CALL_ID = #{vslCallId}	


	AND	SUBSTR(A.VSL_CALL_ID ,3,9) = 'STRG'


	AND	A.SHFT_ID = #{shftId}

AND WORK_YMD = TO_CHAR(TO_DATE(#{workYmd}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND VSL_CALL_ID = #{vslCallId}
AND SUBSTR(A.VSL_CALL_ID ,3,9) = 'STRG'
AND SHFT_ID = #{shftId}
AND (CASE WHEN D.EQ_DIV_CD IN ('FL', 'TR') THEN D.EQ_DIV_CD ELSE 'ME' END) = DIV_CD AND MEGA_SEQ = D.SEQ AND REF_NO = A.REF_NO AND NVL (MBS_CD, ' ') = ( CASE WHEN D.FL_STATUS = 'N' THEN ' ' WHEN C.OPE_COMP IS NOT NULL THEN 'CTR' ELSE 'LAIP' END )
