/* confirmLoadingForRORO.getCargoItems */ WITH WH_BAL_AMOUNT AS ( SELECT SUM (J.PKG_QTY) AS BALQTY, SUM (J.CG_WGT) AS BALMT, SUM (J.CG_VOL) AS BALM3, J.VSL_CALL_ID AS VSL_CALL_ID, J.CG_NO AS CG_NO, G.SHIPG_NOTE_NO, J.DRIVER_ID FROM TMT_JOB J INNER JOIN TMT_GR G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.GR_NO WHERE J.VSL_CALL_ID = #{vslCallId} AND G.SHIPG_NOTE_NO = #{shipgNoteNo} AND J.JOB_PURP_CD IN ('GW', 'AW') GROUP BY J.VSL_CALL_ID, J.CG_NO, G.SHIPG_NOTE_NO, J.DRIVER_ID),
VSL_TO_GATE AS (SELECT SUM (J.PKG_QTY) AS BALQTY, SUM (J.CG_WGT) AS BALMT, SUM (J.CG_VOL) AS BALM3, J.VSL_CALL_ID AS VSL_CALL_ID, J.CG_NO AS CG_NO, (SELECT STRING_AGG(CHAS_NO, ', ') WITHIN GROUP (ORDER BY CHAS_NO) FROM (SELECT DISTINCT CHAS_NO FROM TMT_JOB WHERE VSL_CALL_ID = J.VSL_CALL_ID AND CG_NO = J.CG_NO AND JOB_PURP_CD IN ('GV','AV')) AS chas_no) AS CHAS_NO FROM TMT_JOB J WHERE J.VSL_CALL_ID = #{vslCallId} AND J.JOB_PURP_CD IN ('GV','AV') GROUP BY J.VSL_CALL_ID,J.CG_NO),
GATE_IN_AMOUNT AS (SELECT SUM (J.PKG_QTY) AS BALQTY, SUM (J.CG_WGT) AS BALMT, SUM (J.CG_VOL) AS BALM3, J.VSL_CALL_ID AS VSL_CALL_ID, J.CG_NO AS CG_NO, G.SHIPG_NOTE_NO, J.CHAS_NO, J.DRIVER_ID FROM TMT_JOB J INNER JOIN TMT_GR G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.GR_NO WHERE J.VSL_CALL_ID = #{vslCallId} AND G.SHIPG_NOTE_NO = #{shipgNoteNo} AND J.JOB_PURP_CD IN ('OI') GROUP BY J.VSL_CALL_ID, J.CG_NO, G.SHIPG_NOTE_NO, J.CHAS_NO, J.DRIVER_ID),
APRON_BAL_AMOUNT AS (SELECT J.VSL_CALL_ID, G.SHIPG_NOTE_NO, ISNULL (SUM ( CASE J.JOB_PURP_CD WHEN 'WA' THEN J.CG_WGT ELSE - J.CG_WGT END ), 0) AS BALMT, ISNULL ( SUM ( CASE J.JOB_PURP_CD WHEN 'WA' THEN J.PKG_QTY ELSE - J.PKG_QTY END ), 0) AS BALQTY, ISNULL (SUM ( CASE J.JOB_PURP_CD WHEN 'WA' THEN J.CG_VOL ELSE - J.CG_VOL END ), 0) AS BALM3, STRING_AGG(J.CHAS_NO, ',') WITHIN GROUP ( ORDER BY J.CHAS_NO) AS CHAS_NO FROM TMT_JOB J INNER JOIN TMT_GR G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.GR_NO WHERE J.VSL_CALL_ID = #{vslCallId} AND G.SHIPG_NOTE_NO = #{shipgNoteNo} AND J.JOB_PURP_CD IN ('WA', 'AV') GROUP BY J.VSL_CALL_ID, G.SHIPG_NOTE_NO ), CUSTOMS_RELEASE AS ( SELECT TOP(1) CASE WHEN CUS.DOC_NO IS NULL OR CUS.DOC_NO = '' THEN 'HOLD' ELSE 'RELEASE' END AS status FROM TMT_SHIPG_NOTE SN LEFT OUTER JOIN TMT_CUSTOMS_RELEASE CUS ON CUS.VSL_CALL_ID = SN.VSL_CALL_ID AND CUS.DOC_NO = SN.MF_DOC_ID WHERE SN.VSL_CALL_ID = #{vslCallId} AND SN.SHIPG_NOTE_NO = #{shipgNoteNo} AND ISNULL(CUS.BONDED_WH_YN, 'N') = 'N' ),
getCargoItems AS ( SELECT TOP(1) /* ConfirmLoadingForRORO.getCargoItems */ A.VSL_CD AS VSLCD, G.TSPT_COMP AS TSPTCOMP, A.CALL_YEAR AS CALLYEAR, A.CALL_SEQ AS CALLSEQ, G.GR_NO AS CGNO, G.GR_NO AS GRNO, G.VSL_CALL_ID AS VSLCALLID, G.SHIPG_NOTE_NO AS SHIPGNOTENO, A.MF_DOC_ID AS MFDOCID, P.VSL_NM AS VSLNM, C.STAT_CD AS STAT, ISNULL (CAST (A.PKG_QTY AS NUMERIC(10,0)), 0) AS SNQTY, ISNULL (CAST (REPLACE (ISNULL (A.CG_WGT, 0), ',', '') AS NUMERIC(15,3)), 0) AS SNMT, ISNULL (CAST (A.CG_VOL AS NUMERIC(15,3)), 0) AS SNM3, ISNULL (CAST (G.PKG_QTY AS NUMERIC(10,0)), 0) AS DOCQTY, ISNULL (CAST (REPLACE (ISNULL (G.CG_WGT, 0), ',', '') AS NUMERIC(15,3)), 0) AS DOCMT, ISNULL (CAST (G.CG_VOL AS NUMERIC(15,3)), 0) AS DOCM3,
CASE ISNULL(O.BALQTY, 0) WHEN 0 THEN ISNULL(TRY_CAST(ISNULL(REPLACE(G.PKG_QTY, ',', ''), '0') AS NUMERIC(10,0)), 0) ELSE O.BALQTY END AS QTY, CASE ISNULL(O.BALMT, 0) WHEN 0 THEN ISNULL(CAST(NULLIF(REPLACE(ISNULL(G.CG_WGT, '0'), ',', ''), '') AS NUMERIC(15,3)), 0) ELSE O.BALMT END AS MT, CASE ISNULL(O.BALM3, 0) WHEN 0 THEN ISNULL(CAST(G.CG_VOL AS NUMERIC(15,3)), 0) ELSE O.BALM3 END AS M3, CASE ISNULL(O.BALQTY, 0) WHEN 0 THEN ISNULL(CAST(NULLIF(REPLACE(ISNULL(G.PKG_QTY, '0'), ',', ''), '') AS NUMERIC(10,0)), 0) ELSE O.BALQTY END AS LOADQTY, CASE ISNULL(O.BALMT, 0) WHEN 0 THEN ISNULL(CAST(NULLIF(REPLACE(ISNULL(G.CG_WGT, '0'), ',', ''), '') AS NUMERIC(15,3)), 0) ELSE O.BALMT END AS LOADMT, CASE ISNULL(O.BALM3, 0) WHEN 0 THEN ISNULL(CAST(G.CG_VOL AS NUMERIC(15,3)), 0) ELSE O.BALM3 END AS LOADM3,
CAST (ISNULL (O.BALQTY, 0) AS NUMERIC(10,0)) AS BALQTY, CAST (ISNULL (O.BALMT, 0) AS NUMERIC(15,3)) AS BALMT, CAST (ISNULL (O.BALM3, 0) AS NUMERIC(15,3)) AS BALM3,
CAST (ISNULL (G.PKG_QTY, 0) AS NUMERIC(10,0)) - CAST (ISNULL (VG.BALQTY, 0) AS NUMERIC(10,0)) AS BALQTY, CAST (REPLACE (ISNULL (G.CG_WGT, 0), ',', '') AS NUMERIC(15,3)) - CAST ( REPLACE (ISNULL (VG.BALMT, 0), ',', '') AS NUMERIC(15,3) ) AS BALMT, CAST (ISNULL (G.CG_VOL, 0) AS NUMERIC(15,3)) - CAST (ISNULL (VG.BALM3, 0) AS NUMERIC(15,3)) AS BALM3,
G.TSPT_TP_CD, A.TSPT_COMP AS TSPTR, (SELECT TOP(1) ENG_SNM FROM TMT_PTNR WHERE PTNR_TYPE = 'TRK' AND PTNR_CODE = A.TSPT_COMP ) AS TSPTRNM, A.DELV_TP_CD, DBO.F_CM_CODE_NM ('MT', 'DELVTP', A.DELV_TP_CD) AS DELV_TP_NM, A.CATG_CD, G.CMDT_CD AS CMDTCD, G.PKG_TP_CD AS PKGTPCD, G.PKG_TP_CD AS REPKGTYPECD, A.CG_WGT_UNIT AS WGTUNIT, A.CG_VOL_UNIT AS MSRMTUNIT, A.POD AS PORTOFLOAD, A.POD AS PORTOFDIS, A.FDEST AS FDEST, ISNULL (C.CG_TP_CD, A.CG_TP_CD) AS CG_TP_CD, DBO.F_CM_CODE_NM ('MT', 'CGTP', A.CG_TP_CD) AS CG_TP_NM, A.DELV_TP_CD AS OPDELVTPCD, A.FWRD AS FWRAGNT, A.SHIPG_AGNCY AS SHPGAGENT, (SELECT ISNULL (SUM (JO.PKG_QTY), 0) FROM TMT_JOB JO WHERE JO.VSL_CALL_ID = G.VSL_CALL_ID AND JO.CG_NO = G.GR_NO AND JO.JOB_PURP_CD IN ('GV', 'AV')) AS ACCUSUMQTY, (SELECT ISNULL (SUM (JO.CG_WGT), 0) FROM TMT_JOB JO WHERE JO.VSL_CALL_ID = G.VSL_CALL_ID AND JO.CG_NO = G.GR_NO AND JO.JOB_PURP_CD IN ('GV', 'AV')) AS ACCUSUMWGT, (SELECT ISNULL (SUM (JO.CG_VOL), 0) FROM TMT_JOB JO WHERE JO.VSL_CALL_ID = G.VSL_CALL_ID AND JO.CG_NO = G.GR_NO AND JO.JOB_PURP_CD IN ('GV', 'AV')) AS ACCUSUMMSRMT, A.CNSNE AS CNSNE, A.CNSNE_NM AS CNSNENM, A.SHPR AS SHPR, A.SHPR_NM AS SHPRNM, CUS.STATUS AS CUSTMODE, CASE A.STAT_CD WHEN 'AP' THEN 'Y' ELSE 'N' END AS SNLDYN, A.EACH_WGT AS EACHMT, A.EACH_VOL AS EACHM3, CAST (ISNULL (A1.BALMT, 0) AS NUMERIC(15,3)) AS avBalMt, CAST (ISNULL (A1.BALM3, 0) AS NUMERIC(15,3)) AS avBalM3, CAST (ISNULL (A1.BALQTY, 0) AS NUMERIC(10,0)) AS avBalQty, A.CMDT_GRP_CD AS CMDTGRPCD, ISNULL (G.ADDITIONAL_CHK, 'N') AS additionalCheckYn, ISNULL(A1.CHAS_NO, GI.CHAS_NO) AS UNITNO, VG.CHAS_NO AS VGUNITNO, ISNULL (O.DRIVER_ID, GI.DRIVER_ID) AS DRIVER_ID FROM TMT_SHIPG_NOTE A LEFT OUTER JOIN TMT_GR G ON A.VSL_CALL_ID = G.VSL_CALL_ID AND A.SHIPG_NOTE_NO = G.SHIPG_NOTE_NO LEFT OUTER JOIN TMT_RORO_MST C ON A.VSL_CALL_ID = C.VSL_CALL_ID AND C.GR_NO = G.GR_NO AND C.CG_NO = A.SHIPG_NOTE_NO LEFT OUTER JOIN TMT_VSL_PART P ON A.VSL_CD = P.VSL_CD LEFT OUTER JOIN WH_BAL_AMOUNT O ON A.VSL_CALL_ID = O.VSL_CALL_ID AND A.SHIPG_NOTE_NO = O.SHIPG_NOTE_NO AND G.GR_NO = O.CG_NO LEFT OUTER JOIN GATE_IN_AMOUNT GI ON A.VSL_CALL_ID = GI.VSL_CALL_ID AND A.SHIPG_NOTE_NO = GI.SHIPG_NOTE_NO AND G.GR_NO = GI.CG_NO LEFT OUTER JOIN VSL_TO_GATE VG ON A.VSL_CALL_ID = VG.VSL_CALL_ID AND G.GR_NO = VG.CG_NO LEFT OUTER JOIN APRON_BAL_AMOUNT A1 ON A.VSL_CALL_ID = A1.VSL_CALL_ID AND A.SHIPG_NOTE_NO = A1.SHIPG_NOTE_NO LEFT OUTER JOIN CUSTOMS_RELEASE CUS ON 1=1 WHERE A.VSL_CALL_ID = #{vslCallId} AND A.STAT_CD = 'AP' AND A.CG_TP_CD IN ('RCV')
AND A.SHIPG_NOTE_NO = #{shipgNoteNo}
AND G.GR_NO = #{grNo}
)

