SELECT /* ConfirmLoadingForRORO.getDirectUnitItems */ S.VSL_CALL_ID, S.VSL_CD, S.CALL_YEAR, S.CALL_SEQ, S.MF_DOC_ID, S.SHIPG_NOTE_NO AS CG_NO, S.CATG_CD, S.CG_TP_CD, D.IX_CD, D.NEW_YN, D.DOC_WGT, D.CBM, B.BRAND_CD, M.MODEL_CD, D.CHAS_NO, D.SEQ AS RORO_SEQ, R.DRIVER_ID, R.DRIVER_NM, R.DRIVER_LICENSE, ISNULL (R.TRUCK_NO, '') AS TRUCK_NO, R.LOC_ID,
FORMAT(R.GATE_IN_DTM, 'dd/MM/yyyy mm:hh') AS GATE_IN_DTM, FORMAT(R.IN_DTM, 'dd/MM/yyyy mm:hh') AS IN_DTM, FORMAT(R.OUT_DTM, 'dd/MM/yyyy mm:hh') AS OUT_DTM, FORMAT(R.LOADING_DTM, 'dd/MM/yyyy mm:hh') AS LOADING_DTM,
R.DELV_TP_CD, DBO.F_CM_CODE_NM ('MT', 'DELVTP', R.DELV_TP_CD) AS DELV_TP_NM, R.STAT_CD, DBO.F_CM_CODE_NM ('MT', 'CGSTATUS', R.STAT_CD) AS STAT_NM, R.STEVEDORE_ID, R.YARD_RMK, R.CRANE, R.LOADING_RMK, R.VSL_LOCATION
FROM TMT_SHIPG_NOTE S INNER JOIN TMT_SHIPG_NOTE_DTL D ON S.VSL_CALL_ID = D.VSL_CALL_ID AND S.SHIPG_NOTE_NO = D.SHIPG_NOTE_NO LEFT OUTER JOIN TMT_RORO_MST R ON S.VSL_CALL_ID = R.VSL_CALL_ID AND S.SHIPG_NOTE_NO = R.CG_NO AND D.CHAS_NO = R.CHASSIS_NO
LEFT OUTER JOIN TMT_BRAND B ON D.BRAND_CD = B.BRAND_CD LEFT OUTER JOIN TMT_BRAND_DTL M ON D.MODEL_CD = M.MODEL_CD AND D.BRAND_CD = M.BRAND_CD WHERE S.VSL_CALL_ID = #{vslCallId} AND S.STAT_CD = 'AP' AND S.CG_TP_CD IN ('RCV') AND D.IX_CD = 'X'
AND (R.IN_DTM IS NULL OR R.IN_DTM = '')
AND D.SHIPG_NOTE_NO = #{shipgNoteNo}
AND R.CHASSIS_NO = #{unitNo}
AND R.LOADING_DTM = #{loadingTime}
ORDER BY R.GATE_IN_DTM, R.CHASSIS_NO

