mapper.update:
UPDATE  /* ConfirmLoadingForRORO.updateConfirmYardAndLoadingCheckForRoRo */
	TMT_RORO_MST
SET     
	STAFF_CD 		=  #{userId}
	,UPDATE_TIME 	= SYSDATETIME()
	,STAT_CD 		= #{statCd}

	,OUT_DTM 		= CONVERT(DATETIME, #{outDate},103)
	,LOADING_DTM 	= CONVERT(DATETIME, #{endDtStr},103)

	,DELV_TP_CD 	= #{delvTpCd}
WHERE   
	VSL_CALL_ID 	= #{vslCallId}
	AND CG_NO 		= #{cgNo}
	AND CHASSIS_NO IN ${unitNo}

UPDATE 	 /* ConfirmLoadingForRORO.updateNextJobNoForWAJob */
	TMT_JOB
SET 
	NEXT_JOB_NO = #{jobNo}
WHERE 
		VSL_CALL_ID = #{vslCallId}
		AND JOB_NO = #{prevJobNo}

mapper.select:
SELECT	/* ConfirmLoadingForRORO.selectGateInTimeSeq */
	MIN(AD.SEQ) AS SEQ
FROM 
	TMT_CG_ARRV_DELV AD
WHERE
	AD.CG_NO 			= #{cgNo}
	AND AD.VSL_CALL_ID 	= #{vslCallId}
	AND (AD.GATE_OUT_DT IS NULL OR AD.GATE_OUT_DT = '')
	AND AD.LORRY_NO 	= #{lorryNo}
	AND AD.GATE_TXN_NO 	= #{gateTxnNo}

/* ================ ConfirmLoadingForRORO.selectInDirectUnitItemsHHT ================ */ SELECT R.VSL_CALL_ID, R.VSL_CD, R.CALL_YEAR, R.CALL_SEQ, R.MF_DOC_ID, R.CG_NO, R.CHASSIS_NO AS unitNo, R.BRAND_CD AS brandcd, R.MODEL_CD,
R.DRIVER_ID, R.DRIVER_NM, R.DRIVER_LICENSE, ISNULL(R.TRUCK_NO, '') AS truckNo, R.LOC_ID AS yardLoc,
FORMAT(R.GATE_IN_DTM, 'dd/MM/yyyy mm:hh') AS gateInDate, FORMAT(R.IN_DTM, 'dd/MM/yyyy mm:hh') AS inDate, FORMAT(R.OUT_DTM, 'dd/MM/yyyy mm:hh') AS outDate, FORMAT(R.LOADING_DTM, 'dd/MM/yyyy mm:hh') AS loadingTime,
R.DELV_TP_CD, DBO.F_CM_CODE_NM('MT', 'DELVTP', R.DELV_TP_CD) AS DELV_TP_NM, R.STAT_CD AS statCd, DBO.F_CM_CODE_NM('MT', 'CGSTATUS', R.STAT_CD) AS statNm, R.STEVEDORE_ID, R.YARD_RMK, R.CRANE, R.VSL_LOCATION, R.LOADING_RMK FROM TMT_SHIPG_NOTE S INNER JOIN TMT_RORO_MST R ON S.VSL_CALL_ID = R.VSL_CALL_ID AND S.SHIPG_NOTE_NO = R.CG_NO WHERE S.VSL_CALL_ID = #{vslCallId} AND S.STAT_CD = 'AP' AND R.STAT_CD = #{statCd} AND S.CG_TP_CD IN ('RCV') AND R.IX_CD = 'X' AND R.DELV_TP_CD = 'I' AND (R.IN_DTM IS NOT NULL AND R.IN_DTM <> '')
AND (R.RHDL_NO IS NULL OR R.RHDL_NO = '')
AND R.CG_NO = #{shipgNoteNo}
AND (SELECT BRAND_NM FROM TMT_BRAND WHERE R.BRAND_CD = BRAND_CD ) = #{brandCd}
AND R.CHASSIS_NO = #{unitNo}
AND R.LOADING_DTM = #{loadingTime}
ORDER BY R.IN_DTM, R.CHASSIS_NO

/* ================ ConfirmLoadingForRORO.selectDirectUnitItemsHHT ================ */ SELECT R.VSL_CALL_ID, R.VSL_CD, R.CALL_YEAR, R.CALL_SEQ, R.MF_DOC_ID, R.CG_NO, R.BRAND_CD AS brandCd, R.MODEL_CD AS modelCd,
D.CHAS_NO AS unitNo, D.SEQ AS roroSeq,
R.DRIVER_ID AS driverId, R.DRIVER_NM AS driverNm, R.DRIVER_LICENSE AS driverLicense, ISNULL(R.TRUCK_NO, '') AS truckNo, R.LOC_ID,
FORMAT(R.GATE_IN_DTM, 'dd/MM/yyyy mm:hh') AS gateInDate, FORMAT(R.IN_DTM, 'dd/MM/yyyy mm:hh') AS inDate, FORMAT(R.OUT_DTM, 'dd/MM/yyyy mm:hh') AS outDate, FORMAT(R.LOADING_DTM, 'dd/MM/yyyy mm:hh') AS loadingTime,
R.DELV_TP_CD AS delvTpCd, DBO.F_CM_CODE_NM('MT', 'DELVTP', R.DELV_TP_CD) AS delvTpNm, R.STAT_CD AS statCd, DBO.F_CM_CODE_NM('MT', 'CGSTATUS', R.STAT_CD) AS statNm, R.STEVEDORE_ID, R.YARD_RMK, R.CRANE, R.VSL_LOCATION, R.LOADING_RMK, D.IX_CD, D.NEW_YN, D.DOC_WGT, D.CBM FROM TMT_SHIPG_NOTE S INNER JOIN TMT_SHIPG_NOTE_DTL D ON S.VSL_CALL_ID = D.VSL_CALL_ID AND S.SHIPG_NOTE_NO = D.SHIPG_NOTE_NO LEFT OUTER JOIN TMT_RORO_MST R ON S.VSL_CALL_ID = R.VSL_CALL_ID AND S.SHIPG_NOTE_NO = R.CG_NO AND D.CHAS_NO = R.CHASSIS_NO
WHERE S.VSL_CALL_ID = #{vslCallId} AND S.STAT_CD = 'AP' AND R.STAT_CD = #{statCd} AND S.CG_TP_CD IN ('RCV','RMA') AND D.IX_CD = 'X'
AND (R.IN_DTM IS NULL OR R.IN_DTM = '')
AND R.CG_NO = #{shipgNoteNo}
AND (SELECT BRAND_NM FROM TMT_BRAND WHERE R.BRAND_CD = BRAND_CD ) = #{brandCd}
AND R.CHASSIS_NO = #{unitNo}
AND R.LOADING_DTM = #{loadingTime}
ORDER BY R.GATE_IN_DTM, R.CHASSIS_NO
