mapper.sql:
SELECT DIV, OPECLASSCD, VSLCALLID, BLSN, CGNO, SHIPGAGNT, FWRAGNT, F_CM_012('MT', 'DELVTP', DELVTPCD) AS DELVTPCD, CMDTCD, NVL(GRSMT,0) GRSMT, NVL(GRSM3,0) GRSM3, NVL(GRSQTY,0) GRSQTY, NVL(DIRMT,0) DIRMT, NVL(DIRM3,0) DIRM3, NVL(DIRQTY,0) DIRQTY, NVL(INDMT,0) INDMT, NVL(INDM3,0) INDM3, NVL(INDQTY,0) INDQTY, NVL(NORMT,0) NORMT, NVL(NORM3,0) NORM3, NVL(NORQTY,0) NORQTY, NVL(DMGMT,0) DMGMT, NVL(DMGM3,0) DMGM3, NVL(DMGQTY,0) DMGQTY, NVL(SHUMT,0) SHUMT, NVL(SHUM3,0) SHUM3, NVL(SHUQTY,0) SHUQTY, NVL(GPMT,0) GPMT, NVL(GPM3,0) GPM3, NVL(GPQTY,0) GPQTY, C.ATA FROM (
UNION ALL
) A, TMT_VSL_PART B, TMT_VSL_SCH C WHERE A.DIV IN ('SN','GR','BL') AND C.VSL_CALL_ID = A.VSLCALLID AND C.VSL_CD = B.VSL_CD AND A.OPECLASSCD IS NOT NULL
AND B.SA_CORP_ID = #{ptnrCode} AND SHIPGAGNT = #{ptnrCode}
AND FWRAGNT = #{ptnrCode}
AND A.BLSN = #{blNo}
AND A.BLSN = #{shipgNoteNo}
AND A.DELVTPCD=#{delvTpCd}
AND A.CGNO=#{grNo}
AND A.OPECLASSCD = #{opeClassCd}
AND C.ATA BETWEEN TO_DATE(#{fromDate},'DD/MM/YYYY') AND TO_DATE(#{toDate},'DD/MM/YYYY')

mapper.select:
		SELECT VSL_CALL_ID          VSLCALLID,
		       CG_NO                CGNO,
		       STAT_CD              STATCD
		FROM TMT_CG_MST 
		WHERE VSL_CALL_ID=#{vslCallId}

  			AND SHIPG_NOTE_NO= #{shipgNoteNo}


 			AND BL_NO=#{blNo}
 			AND STAT_CD IN ('OV', 'DV')

SELECT S.VSL_CALL_ID AS VSLCALLID, G.SHIPG_NOTE_NO AS SHIPGNOTENO, S.BERTH_LOC AS BERTHLOC, TO_CHAR(S.ETA, 'DD/MM/YYYY HH24:MI') AS ETA, TO_CHAR(S.ETD, 'DD/MM/YYYY HH24:MI') AS ETD, (SELECT TO_CHAR(MAX(EST_ARRV_DT), 'DD/MM/YYYY HH24:MI') FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = G.SHIPG_NOTE_NO) ESTARRVDT FROM TMT_GR G, TMT_VSL_SCH S WHERE G.VSL_CALL_ID = S.VSL_CALL_ID
AND G.VSL_CALL_ID = #{vslCallId}
AND G.GR_NO = #{grNo}
AND G.GR_NO = #{cgNo}

SELECT S.VSL_CALL_ID VSLCALLID, B.BL_NO AS BLNO, S.BERTH_LOC AS BERTHLOC, TO_CHAR(S.ETA, 'DD/MM/YYYY HH24:MI') AS ETA, TO_CHAR(S.ETD, 'DD/MM/YYYY HH24:MI') AS ETD FROM TMT_VSL_SCH S, TMT_BL B, TMT_CG_ARRV_DELV D WHERE 1=1
AND S.VSL_CALL_ID = #{vslCallId}
AND S.VSL_CALL_ID = D.VSL_CALL_ID AND S.VSL_CALL_ID = B.VSL_CALL_ID AND B.BL_NO = D.CG_NO AND ROWNUM = 1
AND D.GATE_PASS_NO = #{gatePassNo}

SELECT	NVL(MAX(TO_NUMBER(S.JOB_GROUP)),0)+1 AS JOBGROUP 
FROM 	TMT_JOB S
WHERE 	S.VSL_CALL_ID = #{vslCallId}

SELECT	ACTL_DELV_TP_CD Æ°
FROM	TMT_CG_MST
WHERE 	ROWNUM = 1 AND CG_NO = #{cgNo} AND	VSL_CALL_ID = #{vslCallId}

SELECT  DECODE(WH_FNL_DELV_YN,'Y','1', '2') 
FROM 	TMT_CG_MST 
WHERE 	ROWNUM = 1 AND CG_NO = #{cgNo} AND	VSL_CALL_ID = #{vslCallId}

			SELECT '1' FROM TMT_CG_ARRV_DELV
					WHERE ROWNUM = 1 
					AND VSL_CALL_ID = #{vslCallId} 
					AND CG_NO = #{cgNo}
					AND LORRY_NO = #{lorryNo}

   						AND CG_IN_OUT_CD = #{cgInOutCd}


   						AND GATE_TXN_NO = #{gateTxnNo}


					AND GATE_PASS_NO IS  NULL
					AND GATE_OUT_DT IS NULL

SELECT	NVL(TO_CHAR(HDL_OUT_ST_DT),'1') 
FROM 	TMT_CG_MST
WHERE	ROWNUM = 1 AND CG_NO = #{cgNo} AND	VSL_CALL_ID = #{vslCallId}

SELECT LOC_ID AS LOCID, LOC_NM AS LOCNM FROM TMT_LOC_DEF WHERE 1=1
AND LOC_DIV_CD = #{locDivCd}
ORDER BY LOC_NM
