mapper.select:
SELECT	/*cargoLoading.selectGateInTimeSeq*/
	MIN(AD.SEQ) as SEQ
FROM 	
	TMT_CG_ARRV_DELV AD
WHERE
	AD.CG_NO = #{cgNo}
	AND AD.VSL_CALL_ID = #{vslCallId}
	AND AD.GATE_OUT_DT IS NULL OR AD.GATE_OUT_DT =''
	AND AD.LORRY_NO = #{lorryNo}
	AND AD.GATE_TXN_NO = #{gateTxnNo}

mapper.update:
UPDATE /*cargoLoading.updateCgLdAmtItems*/ TMT_CG_MST SET UPDATE_TIME = SYSDATETIME(), STAFF_CD = #{userId}, LORRY_NO = #{lorryNo}, VERSION =#{newVersion}, REPKG_TP_CD = #{rePkgTpCd}
,PKG_QTY = CAST(ISNULL(PKG_QTY,0) AS NUMERIC(10,0)) + CAST(ISNULL(#{loadQty},0) AS NUMERIC(10,0)), CG_WGT = CAST(ISNULL(CG_WGT,0) AS NUMERIC(15,3))+ CAST(ISNULL(#{loadMt},0) AS NUMERIC(15,3)), CG_VOL = CAST(ISNULL(CG_VOL,0) AS NUMERIC(15,3))+ CAST(ISNULL(#{loadM3},0) AS NUMERIC(15,3))
,LD_DMG_QTY = CAST(ISNULL(LD_DMG_QTY,0) AS NUMERIC(10,0)) + CAST(ISNULL(#{loadDmgQty},0) AS NUMERIC(10,0)), LD_DMG_MT = CAST(ISNULL(LD_DMG_MT,0) AS NUMERIC(15,3)) + CAST(ISNULL(#{loadDmgMt},0) AS NUMERIC(15,3)), LD_DMG_M3 = CAST(ISNULL(LD_DMG_M3,0) AS NUMERIC(15,3)) + CAST(ISNULL(#{loadDmgM3},0) AS NUMERIC(15,3))
,LOAD_CNCL_MODE = #{loadCnclMode}
,DMG_YN = #{dmgYn}
,RHDL_MODE = 'N'
,RHDL_MODE = 'Y'
WHERE CG_NO = #{cgNo} AND VSL_CALL_ID = #{vslCallId}

