SET
	UPDATE_TIME = SYSDATETIME()
	,STAFF_CD 	= #{userId}
	,VERSION	= #{newVersion}

		,REPKG_TP_CD 	= #{rePkgTpCd} 
		,REPKG_QTY 		= CAST(ISNULL(REPKG_QTY,0) AS NUMERIC(10,0)) + CAST(ISNULL(#{repkgQty},0) AS NUMERIC(10,0)),
		REPKG_WGT 		= CAST(ISNULL(REPKG_WGT,0) AS NUMERIC(15,3)) + CAST(ISNULL(#{repkgWgt},0) AS NUMERIC(15,3)),
		REPKG_MSRMT 	=  CAST(ISNULL(REPKG_MSRMT,0) AS NUMERIC(15,3)) + CAST(ISNULL(#{repkgMsrmt},0) AS NUMERIC(15,3))

 WHERE	
	CG_NO=#{cgNo} AND VSL_CALL_ID = #{vslCallId}

UPDATE /*cargoLoading.updateCgLoadedDamageItems*/ TMT_CG_MST SET UPDATE_TIME = SYSDATETIME() ,STAFF_CD = #{userId} ,VERSION = #{newVersion}
,LD_DMG_QTY = CAST(ISNULL(LD_DMG_QTY,0) AS NUMERIC(10,0)) + CAST(ISNULL(#{loadDmgQty},0) AS NUMERIC(10,0)), LD_DMG_MT = CAST(ISNULL(LD_DMG_MT,0) AS NUMERIC(15,3)) + CAST(ISNULL(#{loadDmgMt},0) AS NUMERIC(15,3)), LD_DMG_M3 = CAST(ISNULL(LD_DMG_M3,0) AS NUMERIC(15,3)) + CAST(ISNULL(#{loadDmgM3},0) AS NUMERIC(15,3))
WHERE CG_NO=#{cgNo} AND VSL_CALL_ID = #{vslCallId}

UPDATE 	/*cargoLoading.updateNextJobNoForWAJob*/
	TMT_JOB
SET 
	NEXT_JOB_NO = #{jobNo}
WHERE 
	VSL_CALL_ID = #{vslCallId}
	AND JOB_NO = #{prevJobNo}

insert.selectKey:
SELECT 
	ISNULL((
		SELECT 
			TOP(1) J.JOB_NO AS jobNo 
		FROM 
			TMT_JOB J
		WHERE 
			J.VSL_CALL_ID 	= #{vslCallId}
			AND J.CG_NO 	= #{cgNo}
			AND J.JOB_GROUP = #{jobGroup}
			AND J.JOB_CO_CD = #{jobCoCd}
			AND ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(#{spCaCoCd},' ')
	), NULL)

