							WHEN 'true' THEN 'Y'
							WHEN 'Y' THEN 'Y'
							ELSE 'N'
						END,
	RMK 				= #{rmk},
	UPDATE_TIME 		= SYSDATETIME(),
	STAFF_CD 			= #{userId},
	VERSION 			= #{newVersion}
 WHERE	
	VSL_CALL_ID			= #{vslCallId} 
	AND CG_NO			= #{cgNo} 
	AND CG_IN_OUT_CD 	= #{cgInOutCd}
	AND LORRY_NO	 	= #{lorryNo}
	AND GATE_PASS_NO IS NULL OR GATE_PASS_NO =''

