mapper.select:
SELECT /*cargoLoading.selectCargoRhdlLoading*/ /*+ FIRST_ROWS(10)*/ R.NX_VSL_CALL_ID AS VSLCALLID, R.NX_REF_NO AS SHIPGNOTENO, R.NX_CG_NO AS CGNO, R.CG_NO AS ORGCGNO, R.ORG_REF_NO AS ORGBLSN, R.VSL_CALL_ID AS ORGVSLCALLID, R.RHDL_GROUP_NO AS RHDLGROUPNO, (SELECT VSL_NM FROM TMT_VSL_PART WHERE VSL_CD = S.VSL_CD) AS VSLNM, C.STAT_CD AS STAT, C.LOAD_CNCL_MODE AS LOADCNCLMODE, C.DMG_YN AS DMGYN, FORMAT(MAX(C.HDL_IN_END_DT), 'dd/MM/yyyy hh:mm') AS HDLINENDDT,
(SELECT ISNULL(CAST(SUM(AA.PKG_QTY) AS INT),0) FROM TMT_SHIPG_NOTE AA WHERE AA.VSL_CALL_ID = R.NX_VSL_CALL_ID AND AA.SHIPG_NOTE_NO = R.NX_REF_NO) AS SNQTY, (SELECT ISNULL(CAST(REPLACE(ISNULL(SUM(AA.CG_WGT),0),',','') AS NUMERIC(15,3)),0) FROM TMT_SHIPG_NOTE AA WHERE AA.VSL_CALL_ID = R.NX_VSL_CALL_ID AND AA.SHIPG_NOTE_NO = R.NX_REF_NO) AS SNMT, (SELECT ISNULL(CAST(SUM(AA.CG_MSRMT) AS NUMERIC(15,3)),0) FROM TMT_SHIPG_NOTE AA WHERE AA.VSL_CALL_ID = R.NX_VSL_CALL_ID AND AA.SHIPG_NOTE_NO = R.NX_REF_NO) AS SNM3, (SELECT ISNULL(CAST(SUM(GG.PKG_QTY) AS NUMERIC(10,0)),0) FROM TMT_GR GG WHERE GG.GR_NO = R.NX_CG_NO AND GG.VSL_CALL_ID = R.NX_VSL_CALL_ID) AS DOCQTY, (SELECT ISNULL(CAST(REPLACE(ISNULL(SUM(GG.WGT),0),',','') AS NUMERIC(15,3)),0) FROM TMT_GR GG WHERE GG.GR_NO = R.NX_CG_NO AND GG.VSL_CALL_ID = R.NX_VSL_CALL_ID) AS DOCMT, (SELECT ISNULL(CAST(SUM(GG.MSRMT) AS NUMERIC(15,3)),0) FROM TMT_GR GG WHERE GG.GR_NO = R.NX_CG_NO AND GG.VSL_CALL_ID = R.NX_VSL_CALL_ID) AS DOCM3, ISNULL(SUM(R.PKG_QTY),0) AS QTY, ISNULL(SUM(R.WGT),0) AS MT, ISNULL(SUM(R.MSRMT),0) AS M3, ISNULL(SUM(R.PKG_QTY),0) - ISNULL((SELECT ABS(SUM(LC.PKG_QTY)) FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' AND J.JOB_PURP_CD IN ('WA')),0) AS LOADQTY, ISNULL(SUM(R.WGT),0) - ISNULL((SELECT ABS(SUM(LC.WGT)) FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' AND J.JOB_PURP_CD IN ('WA')),0) AS LOADMT, ISNULL(SUM(R.MSRMT),0) - ISNULL((SELECT ABS(SUM(LC.MSRMT)) FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' AND J.JOB_PURP_CD IN ('WA')),0) AS LOADM3, ISNULL(SUM(R.PKG_QTY),0) - ISNULL((SELECT ABS(SUM(LC.PKG_QTY)) FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' AND J.JOB_PURP_CD IN ('WA')),0) AS BALQTY, ISNULL(SUM(R.WGT),0) - ISNULL((SELECT ABS(SUM(LC.WGT)) FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' AND J.JOB_PURP_CD IN ('WA')),0) AS BALMT, ISNULL(SUM(R.MSRMT),0) - ISNULL((SELECT ABS(SUM(LC.MSRMT)) FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' AND J.JOB_PURP_CD IN ('WA')),0) AS BALM3, G.TSPT_TP_CD AS TSPTTPCD, D.LORRY_NO AS LORRYID, D.SEQ AS SEQ, (SELECT ENG_SNM FROM TMT_PTNR WHERE PTNR_TYPE = 'TRK' AND PTNR_CODE = A.TSPT_COMP) AS TSPTRNM, A.TSPT_COMP AS TSPTR, (SELECT R.PLAN_LOC_ID FROM TMT_SPC_REQ R JOIN TMT_SPC_PLAN P ON R.REQ_NO = P.REQ_NO AND R.SEQ = P.REQ_SEQ WHERE R.STAT_CD IN ('CNF') AND R.REQ_TP_CD IN ('SPC') AND R.VSL_CALL_ID = R.NX_VSL_CALL_ID AND R.CG_REF_NO = R.NX_CG_NO AND R.SHIPG_NOTE_NO = R.NX_REF_NO GROUP BY R.PLAN_LOC_ID) AS LOCID, A.DELV_TP_CD AS DELVTPCD, A.CATG_CD AS CATGCD, ISNULL(C.WH_FNL_DELV_YN,'N') AS WHFNLDELVYN, G.CMDT_CD AS CMDTCD, G.PKG_TP_CD AS PKGTPCD, G.PKG_TP_CD AS REPKGTYPECD, A.WGT_UNIT AS WGTUNIT, /*NONEXIST COLUMN*/ A.MSRMT_UNIT AS MSRMTUNIT, A.PORT_OF_LOAD AS PORTOFLOAD, A.PORT_OF_DIS AS PORTOFDIS, A.FDEST AS FDEST, A.CG_TP_CD AS CGTPCD, A.DELV_TP_CD AS OPDELVTPCD, A.FWRD AS FWRAGNT, A.SHIPG_AGNCY AS SHPGAGENT, A.CNTRY_OF_ORG AS CNTRYOFORG, (SELECT ISNULL(SUM(JO.PKG_QTY),0) FROM TMT_CG_MST CG JOIN TMT_JOB JO ON CG.VSL_CALL_ID = JO.VSL_CALL_ID AND CG.CG_NO = JO.CG_NO WHERE CG.SHIPG_NOTE_NO = R.NX_REF_NO AND CG.VSL_CALL_ID = R.NX_VSL_CALL_ID AND JO.VSL_CALL_ID = R.NX_VSL_CALL_ID AND JO.CG_NO IN (SELECT MST.CG_NO FROM TMT_CG_MST MST WHERE MST.VSL_CALL_ID = CG.VSL_CALL_ID AND MST.SHIPG_NOTE_NO = CG.SHIPG_NOTE_NO) AND JO.JOB_PURP_CD IN ('AV')) AS ACCUSUMQTY, (SELECT ISNULL(SUM(JO.WGT),0) FROM TMT_CG_MST CG JOIN TMT_JOB JO ON CG.VSL_CALL_ID = JO.VSL_CALL_ID AND CG.CG_NO = JO.CG_NO WHERE CG.SHIPG_NOTE_NO = R.NX_REF_NO AND CG.VSL_CALL_ID = R.NX_VSL_CALL_ID AND JO.VSL_CALL_ID = R.NX_VSL_CALL_ID AND JO.CG_NO IN (SELECT MST.CG_NO FROM TMT_CG_MST MST WHERE MST.VSL_CALL_ID =CG.VSL_CALL_ID AND MST.SHIPG_NOTE_NO = CG.SHIPG_NOTE_NO) AND JO.JOB_PURP_CD IN ('AV')) AS ACCUSUMWGT, (SELECT ISNULL(SUM(JO.MSRMT),0) FROM TMT_CG_MST CG JOIN TMT_JOB JO ON CG.VSL_CALL_ID = JO.VSL_CALL_ID AND CG.CG_NO = JO.CG_NO WHERE CG.SHIPG_NOTE_NO = R.NX_REF_NO AND CG.VSL_CALL_ID = R.NX_VSL_CALL_ID AND JO.VSL_CALL_ID = R.NX_VSL_CALL_ID AND JO.CG_NO IN (SELECT MST.CG_NO FROM TMT_CG_MST MST WHERE MST.VSL_CALL_ID = CG.VSL_CALL_ID AND MST.SHIPG_NOTE_NO = CG.SHIPG_NOTE_NO) AND JO.JOB_PURP_CD IN ('AV')) AS ACCUSUMMSRMT, A.CNSNE AS CNSNE, A.CNSNE_NM AS CNSNENM, A.SHPR AS SHPR, A.SHPR_NM AS SHPRNM, (SELECT CASE WHEN CUS.DOC_NO IS NULL OR CUS.DOC_NO ='' THEN 'HOLD' WHEN CUS.DOC_NO IS NOT NULL AND CUS.DOC_NO <>'' AND CUS.RELEASE_MT IS NULL THEN 'RELEASE' ELSE 'RELEASE' END AS status FROM TMT_SHIPG_NOTE SN LEFT OUTER JOIN (SELECT DOC_NO, CG_NO, VSL_MANIFEST_NO, SUM (RELEASE_MT) AS RELEASE_MT, SUM (RELEASE_QTY) AS RELEASE_QTY FROM TMT_CUSTOMS_RELEASE GROUP BY DOC_NO, CG_NO, VSL_MANIFEST_NO) CUS ON CUS.VSL_MANIFEST_NO = SN.VSL_CALL_ID AND ((CUS.CG_NO IS NULL OR CUS.CG_NO ='' AND CUS.DOC_NO = SN.MF_DOC_ID) OR (CUS.CG_NO IS NOT NULL AND CUS.CG_NO <>'' AND CUS.CG_NO = SN.SHIPG_NOTE_NO)) WHERE SN.VSL_CALL_ID = A.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = A.SHIPG_NOTE_NO) AS CUSTMODE, S.SHIP_CALL_NO AS SHIPCALLNO, A.CBR_NO AS CBRNO, CASE A.STAT_CD WHEN 'AP' THEN 'Y' ELSE 'N' END AS SNLDYN, CASE WHEN MAX(C.LOAD_END_DT) IS NULL THEN 'N' ELSE 'Y' END AS FNLLOADYN, ISNULL((SELECT COUNT(*) FROM (SELECT SUM(LC.WGT) SUMWGT, SUM(LC.PKG_QTY) PKGQTY, LC.LOC_ID FROM TMT_INV_LOC LC JOIN TMT_JOB J ON LC.JOB_NO = J.JOB_NO AND LC.VSL_CALL_ID = J.VSL_CALL_ID AND LC.CG_NO = J.CG_NO JOIN TMT_RHDL_CG G ON J.VSL_CALL_ID = G.VSL_CALL_ID AND J.CG_NO = G.CG_NO AND J.JOB_CO_CD = G.CG_CO_CD WHERE AND ISNULL(J.SP_CA_CO_CD,' ') = ISNULL(G.SP_CA_CO_CD,' ') AND G.NX_VSL_CALL_ID = #{vslCallId} AND G.NX_CG_NO = #{cgNo} AND G.RHDL_GROUP_NO = #{rhdlGroupNo} AND G.NX_REF_NO = #{shipgNoteNo} AND G.RHDL_MODE = 'C' GROUP BY (LC.LOC_ID)) A WHERE A.SUMWGT > 0 OR A.PKGQTY > 0),0) AS LOCCOUNT, COUNT(R.RHDL_NO) AS RHDLGROUPCNT, (SELECT CASE COUNT(RC.ORG_GR_NO) WHEN 0 THEN 'N' ELSE 'Y' END FROM TMT_RHDL_CG RC WHERE RC.NX_VSL_CALL_ID = #{vslCallId} AND RC.NX_CG_NO =#{cgNo} AND RC.RHDL_GROUP_NO =#{rhdlGroupNo} AND RC.NX_REF_NO = #{shipgNoteNo} AND RC.RHDL_MODE = 'C') AS GRYN, 'C' AS RHDLMODE
,CONVERT(DATETIME,(SELECT FORMAT(CONVERT(date,#{shftDt}, 112) ,'dd/MM/yyyy') + ' '+ CONVERT(VARCHAR, CONCAT(LEFT(S.FM_HHMM, 2), ':', RIGHT(S.FM_HHMM, 2))) AS STARTDT FROM TMT_SHFT S WHERE S.SHFT_METH_CD ='Standard' AND S.VLD_YN = 'Y' AND S.SHFT_ID=#{shftId}), 103) AS SHIFTSTARTDT ,CONVERT(DATETIME,(SELECT CASE WHEN CAST(S.TO_HHMM AS INT) <= CAST(S.FM_HHMM AS INT) THEN CONVERT(VARCHAR(10), DATEADD(DAY, 1, CONVERT(DATE, #{shftDt}, 112)), 103) + ' ' + CONVERT(VARCHAR, CONCAT(LEFT(S.TO_HHMM, 2), ':', RIGHT(S.TO_HHMM, 2))) ELSE FORMAT(CONVERT(date,#{shftDt}, 112) ,'dd/MM/yyyy') + ' ' + CONVERT(VARCHAR, CONCAT(LEFT(S.TO_HHMM, 2), ':', RIGHT(S.TO_HHMM, 2))) END AS ENDDT FROM TMT_SHFT S WHERE S.SHFT_METH_CD ='Standard' AND S.VLD_YN = 'Y' AND S.SHFT_ID = #{shftId}),103) AS SHIFTENDDT
,(SELECT TOP(1) JJ.RMK FROM TMT_JOB JJ WHERE JJ.VSL_CALL_ID = #{vslCallId} AND JJ.CG_NO = #{cgNo} AND JJ.JOB_PURP_CD IN ('GV','WA','WV','AG','GA', 'AV') AND JJ.RMK IS NOT NULL AND JJ.RMK <>'' AND JJ.RHDL_GROUP_NO IS NOT NULL AND JJ.RHDL_GROUP_NO <>'' AND JJ.UPD_DT = (SELECT MAX(J.UPD_DT) FROM TMT_JOB J WHERE J.VSL_CALL_ID = #{vslCallId} AND J.CG_NO = #{cgNo} AND J.JOB_PURP_CD IN ('GV','WA','WV','AG','GA','AV') AND J.RMK IS NOT NULL AND J.RMK <>'' AND J.RHDL_GROUP_NO IS NOT NULL AND J.RHDL_GROUP_NO <>'')) AS RMK FROM TMT_RHDL_CG R, TMT_VSL_SCH S CROSS JOIN TMT_SHIPG_NOTE A LEFT JOIN TMT_GR G ON A.SHIPG_NOTE_NO = G.SHIPG_NOTE_NO AND A.VSL_CALL_ID = G.VSL_CALL_ID LEFT JOIN TMT_CG_MST C ON G.GR_NO = C.CG_NO AND G.VSL_CALL_ID = C.VSL_CALL_ID
LEFT JOIN ( SELECT MIN(AD.SEQ) AS SEQ, AD.LORRY_NO AS LORRY_NO, AD.VSL_CALL_ID AS VSL_CALL_ID, AD.CG_NO AS CG_NO FROM TMT_CG_ARRV_DELV AD WHERE AD.CG_NO = #{cgNo} AND AD.VSL_CALL_ID = #{vslCallId} AND AD.GATE_PASS_NO IS NULL AND AD.GATE_OUT_DT IS NULL GROUP BY AD.LORRY_NO, AD.VSL_CALL_ID, AD.CG_NO ) D ON G.GR_NO = D.CG_NO AND G.VSL_CALL_ID = D.VSL_CALL_ID LEFT JOIN TMT_RHDL_CG R ON G.GR_NO = R.NX_CG_NO AND G.VSL_CALL_ID = R.NX_VSL_CALL_ID WHERE S.VSL_CALL_ID = A.VSL_CALL_ID AND R.NX_CG_NO = #{cgNo} AND R.NX_VSL_CALL_ID = #{vslCallId} AND R.RHDL_GROUP_NO = #{rhdlGroupNo}
AND A.CATG_CD = 'T'
AND A.CATG_CD = 'R'
