mapper.sql:
SELECT DISTINCT /*confirmHandlingInOfRORO.selectSnItems*/ S.VSL_CD, S.CALL_SEQ, S.CALL_YEAR, S.VSL_CALL_ID, S.MF_DOC_ID, S.SHIPG_NOTE_NO AS CG_NO, (SELECT STRING_AGG(CHASSIS_NO, ', ') WITHIN GROUP (ORDER BY CHASSIS_NO) FROM ( SELECT DISTINCT CHASSIS_NO FROM TMT_RORO_MST WHERE VSL_CALL_ID = S.VSL_CALL_ID AND CG_NO = S.SHIPG_NOTE_NO AND GR_NO = GR.GR_NO
AND STAT_CD = 'RS' ) TEMPTB ) AS CHASNO, GR.GR_NO, S.CATG_CD, DBO.F_CM_CODE_NM ('MT', 'CATGTP', S.CATG_CD) AS CATG_NM, S.DELV_TP_CD, DBO.F_CM_CODE_NM ('MT', 'DELVTP', S.DELV_TP_CD) AS DELV_TP_NM, DBO.F_CM_CODE_NM ('MT', 'CGTP', S.CG_TP_CD) AS CG_TP_NM, ISNULL ( CAST (S.PKG_QTY AS FLOAT) , 0) AS SNQTY, ISNULL ( CAST ( REPLACE ( ISNULL (S.CG_WGT, 0) , ',', '') AS FLOAT) , 0) AS SNMT, ISNULL ( CAST (S.CG_VOL AS FLOAT) , 0) AS SNM3, ISNULL ( CAST (GR.PKG_QTY AS FLOAT) , 0) AS GRQTY, ISNULL ( CAST ( REPLACE ( ISNULL (GR.CG_WGT, 0) , ',', '') AS FLOAT) , 0) AS GRMT, ISNULL ( CAST (GR.CG_VOL AS FLOAT) , 0) AS GRM3, ISNULL ( CAST (GR.PKG_QTY AS FLOAT) , 0) AS PKGQTY, ISNULL ( CAST ( REPLACE ( ISNULL (GR.CG_WGT, 0) , ',', '') AS FLOAT) , 0) AS WGT, ISNULL ( CAST (GR.CG_VOL AS FLOAT) , 0) AS MSRMT, S.PKG_QTY - WH_IN.PKG_QTY AS BALQTY, S.CG_WGT - WH_IN.CG_WGT AS BALMT, S.CG_VOL - WH_IN.CG_VOL AS BALM3, ISNULL (G.NO_OF_GI, 0) - ISNULL (H.NO_OF_HI, 0) AS TODO_HI, S.TSPT_COMP, GR.CMDT_CD, GR.PKG_TP_CD, S.CG_TP_CD AS CG_TP_CD, GR.TSPT_TP_CD, S.FWRD, S.SHIPG_AGNCY, WH_IN.PKG_QTY AS ACCUSUMQTY, WH_IN.CG_WGT AS ACCUSUMWGT, WH_IN.CG_VOL AS ACCUSUMMSRMT, S.EACH_WGT, S.EACH_VOL, S.CNSNE, S.CNSNE_NM, S.SHPR, S.SHPR_NM, S.CMDT_GRP_CD FROM TMT_SHIPG_NOTE S INNER JOIN TMT_GR GR ON S.VSL_CALL_ID = GR.VSL_CALL_ID AND S.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO LEFT OUTER JOIN GI_INFO G ON S.VSL_CALL_ID = G.VSL_CALL_ID AND S.SHIPG_NOTE_NO = G.CG_NO LEFT OUTER JOIN HI_INFO H ON S.VSL_CALL_ID = H.VSL_CALL_ID AND S.SHIPG_NOTE_NO = H.CG_NO LEFT OUTER JOIN WH_IN tempWH_IN ON 1 = 1, WH_IN WHERE S.VSL_CALL_ID = #{vslCallId} AND S.CATG_CD = 'E' AND S.STAT_CD IN ('AP')
AND S.SHIPG_NOTE_NO = #{shipgNoteNo}
AND GR.GR_NO = #{grNo}
AND S.CHAS_NO LIKE CONCAT(#{unitNo}, '%')
AND S.CG_TP_CD = #{cgTpCd}
AND S.CG_TP_CD IN ('RCV')

mapper.select:
SELECT /*confirmHandlingInOfRORO.selectHandlingInItems*/ R.VSL_CALL_ID, R.VSL_CD, R.CALL_SEQ, R.CALL_YEAR, R.MF_DOC_ID, R.CG_NO, R.CHAS_NO, --Invalid column name 'CHAS_NO'. R.RORO_SEQ, --Invalid column name 'RORO_SEQ'. R.DELV_TP_CD, R.HI_RMK, R.LOC_ID, R.BRAND_CD, R.MODEL_CD, R.DRIVER_ID, R.DRIVER_NM, R.DRIVER_LICENSE, ISNULL(R.TRUCK_NO, '') AS TRUCK_NO, FORMAT(R.GATE_IN_DTM, 'dd/MM/yyyy HH:mm') AS GATE_IN_DTM, FORMAT(R.IN_DTM, 'dd/MM/yyyy HH:mm') AS IN_DTM, FORMAT(R.OUT_DTM, 'dd/MM/yyyy HH:mm') AS OUT_DTM, '' AS GR_NO, '' AS GATE_TICKET_NO, R.RHDL_NO, FORMAT(R.GATE_OUT_DTM, 'dd/MM/yyyy HH:mm') AS GATE_OUT_DTM FROM TMT_SHIPG_NOTE S INNER JOIN TMT_RORO_MST R ON S.VSL_CALL_ID = R.VSL_CALL_ID AND S.SHIPG_NOTE_NO = R.CG_NO
WHERE S.VSL_CALL_ID = #{vslCallId} AND S.STAT_CD IN ('AP') AND S.CG_TP_CD IN ('RCV') AND R.IX_CD = 'X' AND (R.IN_DTM IS NOT NULL AND R.IN_DTM <> '') AND ((R.STAT_CD = 'RH') OR (R.STAT_CD != 'RH' AND R.RHDL_NO IS NULL))
AND R.CG_NO = #{shipgNoteNo}
AND R.CHAS_NO = #{unitNo}
ORDER BY R.IN_DTM, R.CHAS_NO

mapper.update:
UPDATE /*confirmHandlingInOfRORO.updateConfirmHandlingInForRoRo*/ TMT_RORO_MST SET CATG_CD = #{catgCd}, STAT_CD = #{statCd}, DELV_TP_CD = #{delvTpCd}, IN_DTM = ( CASE WHEN (#{inDate} IS NULL OR #{inDate} = '') THEN SYSDATETIME() ELSE CONVERT(DATETIME, #{inDate}, 103) END ), HI_RMK = #{hiRemarks}, LOC_ID = #{locId},
WH_ID = (CASE WHEN (#{locId} IS NULL OR #{locId} = '') THEN NULL ELSE SUBSTRING(#{locId}, 1, CHARINDEX('(', #{locId}) - 1) END), STAFF_CD = #{userId}, UPDATE_TIME = SYSDATETIME()
WHERE VSL_CALL_ID = #{vslCallId} AND CG_NO = #{shipgNoteNo} AND CHASSIS_NO IN ${unitNo}

UPDATE /*confirmHandlingInOfRORO.updateGOJobWithDriverModeForRoRo*/
	TMT_RORO					-- Invalid object name 'TMT_RORO'.
SET
	GATE_OUT_DTM 	= NULL,
	UPD_USER_ID 	= #{userId},
	UPDATE_TIME 	= SYSDATETIME()
WHERE
	VSL_CALL_ID 	= #{vslCallId}
    AND CG_NO 		= #{shipgNoteNo}
    AND CHAS_NO 	= #{unitNo}
    AND RORO_SEQ	= #{roroSeq}

UPDATE /*confirmHandlingInOfRORO.updateCorrectionNoForWB*/
	TMT_WEIGHBRIDGE				 -- Invalid object name 'TMT_WEIGHBRIDGE'.
SET
	UNIT_NO 		= #{correctionNo}
WHERE
	VSL_CALL_ID 	= #{vslCallId}
	AND GR_NO 		= #{grNo}
	AND UNIT_NO 	= #{unitNo}

UPDATE /*confirmHandlingInOfRORO.updateConfirmHandlingInForRoRoHHT*/ TMT_RORO_MST SET STAT_CD = #{statCd}, DELV_TP_CD = #{delvTpCd}, IN_DTM = CONVERT(DATETIME, #{inDate}), HI_RMK = #{hiRemarks}, LOC_ID = #{yardLoc}, WH_ID = (CASE WHEN (#{yardLoc} IS NULL OR #{yardLoc} = '') THEN NULL ELSE SUBSTRING(#{yardLoc}, 0, CHARINDEX('-', #{yardLoc}) - 1) END)
,GATE_OUT_DTM = SYSDATETIME() ,OUT_DTM = SYSDATETIME()
,DRIVER_ID = #{driverId}
,DRIVER_NM = #{driverNm}
,TRUCK_NO = #{truckNo}
,STAFF_CD = #{userId}, UPDATE_TIME = SYSDATETIME(), CORRECTION_UNIT_YN = #{correctionUnitNoYN}, CHAS_NO = --Invalid column name 'CHAS_NO'. (CASE WHEN #{correctionUnitNoYN} = 'Y' THEN #{correctionNo} ELSE #{unitNo} END) WHERE VSL_CALL_ID = #{vslCallId} AND CG_NO = #{shipgNoteNo} AND CHAS_NO = #{unitNo} --Invalid column name 'CHAS_NO'.
AND RORO_SEQ = #{roroSeq} --Invalid column name 'RORO_SEQ'.

UPDATE /*confirmHandlingInOfRORO.updateHIGPArrvDelvItems*/
	TMT_CG_ARRV_DELV
SET
	GATE_PASS_NO 		= #{gatePassNo},
	UPDATE_TIME 		= SYSDATETIME(),
	STAFF_CD 			= #{userId},
	VERSION 			= #{newVersion},
	JOB_NO 				= #{jobNo}
WHERE
	VSL_CALL_ID			= #{vslCallId}
   	AND CG_NO			= #{shipgNoteNo}
	AND CG_IN_OUT_CD 	= #{cgInOutCd}
	AND LORRY_NO 		= #{lorryId}

		AND GATE_TXN_NO = #{gateTxnNo}

	AND (GATE_PASS_NO IS NULL OR GATE_PASS_NO = '')

UPDATE /*confirmHandlingInOfRORO.updateHIGateInTime*/
	TMT_CG_ARRV_DELV
SET
