	UPDATE_TIME 		= SYSDATETIME(),
	STAFF_CD 			= #{userId},
	VERSION 			= #{newVersion}
WHERE
	VSL_CALL_ID			= #{vslCallId}
	AND CG_NO			= #{shipgNoteNo}
	AND CG_IN_OUT_CD 	= #{cgInOutCd}
	AND SEQ 			= CAST(#{seq} AS INT)

mapper.select:
SELECT DISTINCT /* rehandlingOfRORO.selectROROUnitItems */ B.VSL_CALL_ID AS vslCallId, B.VSL_CD AS vslCd, B.CALL_YEAR AS callSeq, B.CALL_SEQ AS callYear, B.MF_DOC_ID AS bookingNo, B.CG_NO AS shipgNoteNo, B.CHASSIS_NO AS unitNo, B.GR_NO AS grNo, B.RORO_SEQ AS roroSeq, B.IX_CD AS ixCd, B.BRAND_CD AS brandCd, B.MODEL_CD AS modelCd, B.DOC_WGT AS docWgt, B.CBM AS cbm, B.NEW_YN AS newYn, B.CATG_CD AS catgCd, B.CG_TP_CD AS cgTpCd FROM TMT_RORO_MST B WHERE B.VSL_CALL_ID = #{vslCallId}
AND B.MF_DOC_ID = #{bookingNo}
AND B.CG_NO = #{shipgNoteNo}
AND B.GR_NO = #{grNo}
AND B.CHASSIS_NO IN ${unitNo}
AND B.CHASSIS_NO IS NOT NULL ORDER BY B.CHASSIS_NO ASC
