WHERE 1 = 1
AND I.VSL_CALL_ID = #{vslCallId}
AND I.VSL_CALL_ID <> 'NonCallId'
AND I.WH_LOC_ID = #{whLocId}
AND (SELECT MF_DOC_ID FROM TMT_BL WHERE BL_NO = I.CG_NO) = #{mfDocId}
AND I.CG_NO = #{blNo}
AND I.CG_NO = #{grNo}
AND EXISTS ( SELECT SN.SHIPG_NOTE_NO, GR.GR_NO FROM TMT_SHIPG_NOTE SN, TMT_GR GR WHERE SN.SHIPG_NOTE_NO IS NOT NULL AND SN.EST_ARRV_DT BETWEEN CONVERT(DATETIME, #{estArrvDtFrom}+' 00:00',103) AND CONVERT(DATETIME, #{estArrvDtTo}+' 23:59',103) AND SN.SHIPG_NOTE_NO = #{snNo} AND SN.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO AND GR.GR_NO = I.CG_NO )
AND EXISTS ( SELECT SN.SHIPG_NOTE_NO, GR.GR_NO FROM TMT_SHIPG_NOTE SN, TMT_GR GR WHERE SN.SHIPG_NOTE_NO IS NOT NULL AND SN.EST_ARRV_DT BETWEEN CONVERT(DATETIME, #{estArrvDtFrom}+' 00:00',103) AND CONVERT(DATETIME, #{estArrvDtTo}+' 23:59',103) AND SN.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO AND GR.GR_NO = I.CG_NO )
AND EXISTS ( SELECT SN.SHIPG_NOTE_NO, GR.GR_NO FROM TMT_SHIPG_NOTE SN, TMT_GR GR WHERE SN.SHIPG_NOTE_NO IS NOT NULL AND SN.SHIPG_NOTE_NO = #{snNo} AND SN.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO AND GR.GR_NO = I.CG_NO )
AND EXISTS (SELECT GR.SHIPG_NOTE_NO FROM TMT_GR GR WHERE GR.GR_NO = I.CG_NO AND GR.SHIPG_NOTE_NO = #{snNo} )

SELECT C.OPE_CLASS_CD OPECLASSCD, DBO.F_CM_CODE_NM('MT', 'CATGTP', C.OPE_CLASS_CD) OPECLASSNM, A.VSLCALLID VSLCALLID, CASE WHEN A.VSLCALLID = 'NonCallId' THEN '-' ELSE A.VSLCALLID END AS VSLCALLIDDSP, (SELECT CASE WHEN COUNT(*) = 0 THEN A.CGNO ELSE MAX(SHIPG_NOTE_NO) END FROM TMT_GR WHERE GR_NO = A.CGNO) AS SNBLNO, (SELECT TOP(1) MF_DOC_ID FROM TMT_BL WHERE BL_NO = (SELECT CASE WHEN COUNT(*) = 0 THEN A.CGNO ELSE MAX(SHIPG_NOTE_NO) END FROM TMT_GR WHERE GR_NO = A.CGNO) ) AS MFDOCID, (SELECT CASE WHEN COUNT(*) = 0 THEN '' ELSE A.CGNO END FROM TMT_GR WHERE GR_NO = A.CGNO) AS GRNO,
A.CGNO CGNO, A.WHTPCD WHTPCD, DBO.F_CM_CODE_NM('MT', 'CGCOCD', A.WHTPCD) WHTPNM, B.SPCACOCD SPCACOCD, B.RCYN RCYN, DBO.F_CM_CODE_NM('MT', 'SPCACOCD', B.SPCACOCD) SPCACONM, C.FWR_AGNT FWRAGNT, B.WGT WGT, B.MSRMT MSRMT, B.PKGQTY PKGQTY, A.LOCID LOCID, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) SHPR FROM tmt_bl WHERE BL_NO = B.CGNO ) ELSE ( SELECT TOP(1) SHPR FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT CASE COUNT (*) WHEN 0 THEN A.CGNO ELSE MAX (SHIPG_NOTE_NO) END FROM TMT_GR WHERE GR_NO = A.CGNO) ) END AS shpr, CASE WHEN C.OPE_CLASS_CD = 'I' THEN ( SELECT top(1) SHPR_NM FROM tmt_bl WHERE BL_NO = B.CGNO ) ELSE ( SELECT top(1) SHPR_NM FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT CASE COUNT (*) WHEN 0 THEN A.CGNO ELSE MAX (SHIPG_NOTE_NO) END FROM TMT_GR WHERE GR_NO = A.CGNO) ) END AS shprName, CASE WHEN C.OPE_CLASS_CD = 'I' THEN ( SELECT top(1) CNSNE FROM tmt_bl WHERE BL_NO = B.CGNO ) ELSE ( SELECT top(1) CNSNE FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT CASE COUNT (*) WHEN 0 THEN A.CGNO ELSE MAX (SHIPG_NOTE_NO) END FROM TMT_GR WHERE GR_NO = A.CGNO) ) END AS cnsne, CASE WHEN C.OPE_CLASS_CD = 'I' THEN ( SELECT TOP(1) CNSNE_NM FROM tmt_bl WHERE BL_NO = B.CGNO ) ELSE ( SELECT top(1) CNSNE_NM FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT CASE COUNT (*) WHEN 0 THEN A.CGNO ELSE MAX (SHIPG_NOTE_NO) END FROM TMT_GR WHERE GR_NO = A.CGNO) ) END AS cnsneName, (SELECT TOP(1) 'Y' FROM TMT_RHDL_CG WHERE CG_CO_CD = A.WHTPCD AND SP_CA_CO_CD = B.SPCACOCD AND VSL_CALL_ID = A.VSLCALLID AND CG_NO = A.CGNO ) RHDLYN FROM ( SELECT VSLCALLID, CGNO, WHTPCD, STRING_AGG(LOCID, ',') WITHIN GROUP (ORDER BY LOCID) AS LOCID FROM ( SELECT VSLCALLID, CGNO, WHTPCD, LOCID, ROW_NUMBER() OVER(PARTITION BY VSLCALLID, CGNO, WHTPCD ORDER BY LOCID) AS CNT FROM ( SELECT I.VSL_CALL_ID AS VSLCALLID, I.CG_NO AS CGNO, I.WH_TP_CD AS WHTPCD, I.WH_LOC_ID AS LOCID, SUM(I.CG_WGT) AS WGT, SUM(I.CG_VOL) AS MSRMT, SUM(I.PKG_QTY) AS PKGQTY FROM TMT_INV_LOC I, TMT_JOB J
AND I.JOB_NO = J.JOB_NO GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_TP_CD, I.WH_LOC_ID ) AS InnerQuery WHERE (InnerQuery.WGT <> 0 OR InnerQuery.MSRMT <> 0 OR InnerQuery.PKGQTY <> 0) ) AS t GROUP BY VSLCALLID, CGNO, WHTPCD ) A, ( SELECT VSLCALLID, CGNO, WHTPCD, WGT, MSRMT, PKGQTY, ISNULL((SELECT MAX(SP_CA_CO_CD) FROM TMT_JOB WHERE VSL_CALL_ID = VSLCALLID AND CG_NO = CGNO AND JOB_CO_CD = WHTPCD AND SP_CA_CO_CD IS NOT NULL), ' ') SPCACOCD, ISNULL((SELECT MAX('Y') FROM TMT_JOB WHERE VSL_CALL_ID = VSLCALLID AND CG_NO = CGNO AND JOB_CO_CD = WHTPCD AND JOB_TP_CD = 'RC'), 'N') RCYN FROM ( SELECT I.VSL_CALL_ID VSLCALLID, I.CG_NO CGNO, I.WH_TP_CD WHTPCD, SUM(I.CG_WGT) WGT, SUM(I.CG_VOL) MSRMT, SUM(I.PKG_QTY) PKGQTY FROM TMT_INV_LOC I, TMT_JOB J
AND I.JOB_NO = J.JOB_NO GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_TP_CD ) sub WHERE (WGT <> 0 OR MSRMT <> 0 OR PKGQTY <> 0) ) B, TMT_CG_MST C WHERE A.VSLCALLID = B.VSLCALLID AND A.CGNO = B.CGNO AND A.WHTPCD = B.WHTPCD AND B.VSLCALLID = C.VSL_CALL_ID AND B.CGNO = C.CG_NO
AND C.OPE_CLASS_CD = #{category}
AND C.FWR_AGNT = #{fwAgent}
AND C.CNSNE = #{cnsne}
AND C.SHPR = #{shpr}
AND C.VSL_CALL_ID = #{vslCallId}

WITH CHANGE_VSL_INFO AS ( SELECT R.VSL_CALL_ID, R.ORG_REF_NO, SUM(INV.CG_WGT) WGT, SUM(INV.CG_VOL) MSRMT, SUM (INV.PKG_QTY) PKGQTY, INV.LOC_ID, INV.CG_NO FROM TMT_RHDL_CG R INNER JOIN TMT_INV_LOC INV ON INV.VSL_CALL_ID = R.NX_VSL_CALL_ID AND INV.REF_NO = R.NX_REF_NO WHERE R.RHDL_MODE = 'C' GROUP BY R.VSL_CALL_ID, R.ORG_REF_NO, INV.LOC_ID, INV.CG_NO ),
WH_BALANCE AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, SUM (I.CG_WGT) WGT, SUM (I.CG_VOL) MSRMT, SUM (I.PKG_QTY) PKGQTY, I.LOC_ID LOC_ID FROM TMT_INV_LOC I
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.LOC_ID, I.WH_TP_CD HAVING SUM (I.CG_WGT) != 0 OR SUM (I.CG_VOL) != 0 OR SUM (I.PKG_QTY) != 0),
WH_BALANCE_RC AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, SUM (I.CG_WGT) WGT, SUM (I.CG_VOL) MSRMT, SUM (I.PKG_QTY) PKGQTY FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON I.VSL_CALL_ID = J.VSL_CALL_ID AND I.CG_NO = J.CG_NO AND I.JOB_NO = J.JOB_NO
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD),
JOB_INFO AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_TP_CD, SUM (I.CG_WGT) WGT, SUM (I.CG_VOL) MSRMT, SUM (I.PKG_QTY) PKGQTY, ISNULL( (SELECT MAX ('Y') FROM TMT_JOB WHERE VSL_CALL_ID = I.VSL_CALL_ID AND CG_NO = I.CG_NO AND JOB_TP_CD = 'RC'), 'N') RCYN FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON I.JOB_NO = J.JOB_NO
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
GROUP BY I.VSL_CALL_ID, I.CG_NO,I.WH_TP_CD),
RC_JOB_INFO AS ( SELECT I.VSL_CALL_ID, I.CG_NO, J.RC_CO_CD, MAX(J.RMK) AS RMK, SUM(I.CG_WGT) WGT, SUM(I.CG_VOL) MSRMT, SUM(I.PKG_QTY) PKGQTY FROM TMT_JOB J INNER JOIN TMT_INV_LOC I ON J.VSL_CALL_ID = I.VSL_CALL_ID AND J.CG_NO = I.CG_NO AND J.JOB_NO = I.JOB_NO WHERE JOB_TP_CD = 'RC'
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
GROUP BY I.VSL_CALL_ID, I.CG_NO, J.RC_CO_CD), IN_JOB_INFO AS ( SELECT AA.VSL_CALL_ID, AA.CG_NO, AA.WH_LOC_ID, AA.WH_TP_CD, AA.WGT, AA.MSRMT, AA.PKGQTY, (SELECT MAX(WH_LOC_TP) FROM TMT_INV_LOC WHERE VSL_CALL_ID = AA.VSL_CALL_ID AND CG_NO = AA.CG_NO AND WH_LOC_ID = AA.WH_LOC_ID) WH_LOC_TP, AA.LOC_ID FROM (SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, SUM(I.CG_WGT) WGT, SUM(I.CG_VOL) MSRMT, SUM(I.PKG_QTY) PKGQTY, I.LOC_ID FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON J.VSL_CALL_ID = I.VSL_CALL_ID AND J.CG_NO = I.CG_NO AND J.JOB_NO = I.JOB_NO WHERE 1=1
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
AND (J.JOB_PURP_CD IN ('AW', 'GW') OR (J.JOB_PURP_CD IN ('WW') AND J.JOB_TP_CD = 'MV') OR (J.JOB_PURP_CD IN ('WW') AND J.JOB_TP_CD = 'RC')) GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, I.LOC_ID) AA ),
IN_JOB_INFO_RC AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, I.WH_LOC_TP, SUM(I.CG_WGT) WGT, SUM(I.CG_VOL) MSRMT, SUM(I.PKG_QTY) PKGQTY FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON J.VSL_CALL_ID = I.VSL_CALL_ID AND J.CG_NO = I.CG_NO AND J.JOB_NO = I.JOB_NO WHERE 1=1
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
AND (J.JOB_PURP_CD IN ('AW', 'GW') OR (J.JOB_PURP_CD IN ('WW') AND J.JOB_TP_CD = 'MV') OR (J.JOB_PURP_CD IN ('WW') AND J.JOB_TP_CD = 'RC')) GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, I.WH_LOC_TP ),
OUT_JOB_INFO AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, SUM (J.CG_WGT) WGT, SUM (J.CG_VOL) MSRMT, SUM (J.PKG_QTY) PKGQTY FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON J.VSL_CALL_ID = I.VSL_CALL_ID AND J.CG_NO = I.CG_NO AND J.JOB_NO = I.JOB_NO WHERE J.JOB_PURP_CD IN ('WG', 'WA')
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD ),
OUT_JOB_INFO_RC AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, SUM (J.CG_WGT) WGT, SUM (J.CG_VOL) MSRMT, SUM (J.PKG_QTY) PKGQTY FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON J.VSL_CALL_ID = I.VSL_CALL_ID AND J.CG_NO = I.CG_NO AND J.JOB_NO = I.JOB_NO WHERE J.JOB_PURP_CD IN ('WG', 'WA')
AND I.VSL_CALL_ID = #{vslCallId}
AND I.SHIP_CALL_NO = #{scn}
GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD ),
RHDL_JOB_INFO AS ( SELECT I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD, SUM(J.CG_WGT) AS WGT, SUM (J.CG_VOL) MSRMT, SUM (J.PKG_QTY) PKGQTY
FROM TMT_RHDL_CG R INNER JOIN TMT_JOB J ON R.RHDL_NO = J.RHDL_NO INNER JOIN TMT_INV_LOC I ON J.JOB_NO = I.JOB_NO
WHERE J.JOB_PURP_CD IN ('GW') AND ((R.RHDL_MODE = 'R' AND I.CG_NO NOT IN (SELECT GR_NO FROM TMT_GR WHERE RHDL_MODE = 'R')) OR (R.RHDL_MODE = 'C'))
AND (R.VSL_CALL_ID = #{vslCallId} OR R.NX_VSL_CALL_ID = #{vslCallId})
AND (R.SHIP_CALL_NO = #{scn})
GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_LOC_ID, I.WH_TP_CD ), getWarehouseReconcileItems AS ( SELECT DISTINCT C.VSL_CALL_ID, P.VSL_NM, C.OPE_CLASS_CD, DBO.F_CM_CODE_NM ('MT', 'CATGTP', C.OPE_CLASS_CD) OPE_CLASS_NM, B.BL_NO, B.MF_DOC_ID AS M_BL_NO, S.SHIPG_NOTE_NO, S.MF_DOC_ID AS BOOKING_NO, ISNULL(B.MF_DOC_ID, S.MF_DOC_ID) AS mfDocId, C.CG_NO, J.RCYN, J.WH_TP_CD, DBO.F_CM_CODE_NM ('MT', 'CGCOCD', J.WH_TP_CD) WH_TP_NM, R.RC_CO_CD, DBO.F_CM_CODE_NM ('MT', 'RCCOCD', R.RC_CO_CD) RC_CO_NM,
ISNULL(B.CMDT_CD, S.CMDT_CD) AS CMDT_CD, DBO.F_GET_CMDT_DESC (ISNULL(B.CMDT_CD, S.CMDT_CD)) AS CMDT_DESC, ISNULL(B.LOT_NO, S.LOT_NO) AS LOT_NO, I.WH_LOC_TP, DBO.F_CM_CODE_NM ('MT', 'WHTP', I.WH_LOC_TP) WH_LOC_TP_NM,
ISNULL(I.WGT, 0) AS STORED_MT, ISNULL(I.MSRMT, 0) AS STORED_M3, ISNULL(I.PKGQTY, 0) AS STORED_QTY,
CASE WHEN ISNULL(O.WGT, 0) = 0 THEN ISNULL(ORC.WGT, 0) ELSE ISNULL(O.WGT, 0) END AS ACT_MT, CASE WHEN ISNULL(O.MSRMT, 0) = 0 THEN ISNULL(ORC.MSRMT, 0) ELSE ISNULL(O.MSRMT, 0) END AS ACT_M3, CASE WHEN ISNULL(O.PKGQTY, 0) = 0 THEN ISNULL(ORC.PKGQTY, 0) ELSE ISNULL(O.PKGQTY, 0) END AS ACT_QTY,
ISNULL(R.WGT, 0) AS RC_MT, ISNULL(R.MSRMT, 0) AS RC_M3, ISNULL(R.PKGQTY, 0) AS RC_QTY,
ISNULL((SELECT TOP(1) CG_WGT FROM TMT_RHDL_CG WHERE VSL_CALL_ID = C.VSL_CALL_ID AND (ORG_REF_NO = C.BL_NO OR ORG_REF_NO = C.SHIPG_NOTE_NO) ), 0) RHDL_MT, ISNULL((SELECT TOP(1) CG_VOL FROM TMT_RHDL_CG WHERE VSL_CALL_ID = C.VSL_CALL_ID AND (ORG_REF_NO = C.BL_NO OR ORG_REF_NO = C.SHIPG_NOTE_NO) ), 0) RHDL_M3, ISNULL((SELECT TOP(1) PKG_QTY FROM TMT_RHDL_CG WHERE VSL_CALL_ID = C.VSL_CALL_ID AND (ORG_REF_NO = C.BL_NO OR ORG_REF_NO = C.SHIPG_NOTE_NO) ), 0) RHDL_QTY,
SUM(ISNULL(W.WGT, 0)) AS BAL_MT, SUM(ISNULL(W.MSRMT, 0)) AS BAL_M3, SUM(ISNULL(W.PKGQTY, 0)) AS BAL_QTY,
(SELECT STRING_AGG(W1.WH_LOC_ID, ', ') WITHIN GROUP (ORDER BY W1.VSL_CALL_ID, W1.CG_NO) AS LOC_ID FROM IN_JOB_INFO W1 WHERE W1.VSL_CALL_ID = C.VSL_CALL_ID AND W1.CG_NO = C.CG_NO) AS LOC_ID, ISNULL(B.SHPR, S.SHPR) AS SHPR, DBO.F_GET_PARTNER_INFO(ISNULL(B.SHPR, S.SHPR), 'ENG_SNM') AS SHPR_NM, ISNULL(B.CG_TP_CD, S.CG_TP_CD) AS CG_TP_CD, ISNULL(B.PROJECT_CARGO , S.PROJECT_CARGO) AS PROJECT_CARGO,
ISNULL(B.CNSNE, S.CNSNE) AS CNSNE, DBO.F_GET_PARTNER_INFO(ISNULL(B.CNSNE, S.CNSNE), 'ENG_SNM') AS CNSNE_NM, ISNULL((SELECT TOP(1) 'Y' FROM TMT_RHDL_CG WHERE NX_VSL_CALL_ID = C.VSL_CALL_ID AND (NX_REF_NO = C.BL_NO OR NX_REF_NO = C.SHIPG_NOTE_NO) ), 'N') RHDLYN, R.RMK, I.LOC_ID AS WH_LOCATION FROM TMT_CG_MST C LEFT OUTER JOIN WH_BALANCE W ON C.VSL_CALL_ID = W.VSL_CALL_ID AND C.CG_NO = W.CG_NO LEFT OUTER JOIN WH_BALANCE_RC WRC ON C.VSL_CALL_ID = WRC.VSL_CALL_ID AND C.CG_NO = WRC.CG_NO LEFT OUTER JOIN RC_JOB_INFO R ON C.VSL_CALL_ID = R.VSL_CALL_ID AND C.CG_NO = R.CG_NO LEFT OUTER JOIN IN_JOB_INFO I ON C.VSL_CALL_ID = I.VSL_CALL_ID AND C.CG_NO = I.CG_NO AND W.WH_LOC_ID = I.WH_LOC_ID LEFT OUTER JOIN IN_JOB_INFO_RC IRC ON C.VSL_CALL_ID = IRC.VSL_CALL_ID AND C.CG_NO = IRC.CG_NO AND IRC.WH_LOC_ID = IRC.WH_LOC_ID LEFT OUTER JOIN OUT_JOB_INFO O ON C.VSL_CALL_ID = O.VSL_CALL_ID AND C.CG_NO = O.CG_NO AND I.WH_LOC_ID = O.WH_LOC_ID LEFT OUTER JOIN OUT_JOB_INFO_RC ORC ON C.VSL_CALL_ID = ORC.VSL_CALL_ID AND C.CG_NO = ORC.CG_NO AND IRC.WH_LOC_ID = ORC.WH_LOC_ID LEFT OUTER JOIN RHDL_JOB_INFO R1 ON C.VSL_CALL_ID = R1.VSL_CALL_ID AND C.CG_NO = R1.CG_NO LEFT OUTER JOIN JOB_INFO J ON C.VSL_CALL_ID = J.VSL_CALL_ID AND C.CG_NO = J.CG_NO LEFT OUTER JOIN TMT_BL B ON C.VSL_CALL_ID = B.VSL_CALL_ID AND C.CG_NO = B.BL_NO LEFT OUTER JOIN TMT_SHIPG_NOTE S ON C.VSL_CALL_ID = S.VSL_CALL_ID AND C.SHIPG_NOTE_NO = S.SHIPG_NOTE_NO LEFT OUTER JOIN TMT_VSL_SCH V ON C.VSL_CALL_ID = V.VSL_CALL_ID LEFT OUTER JOIN TMT_VSL_PART P ON V.VSL_CD = P.VSL_CD LEFT OUTER JOIN CHANGE_VSL_INFO CHGVSL ON I.VSL_CALL_ID = CHGVSL.VSL_CALL_ID AND (CASE WHEN C.SHIPG_NOTE_NO IS NOT NULL THEN C.SHIPG_NOTE_NO ELSE C.BL_NO END) = CHGVSL.ORG_REF_NO
WHERE 1=1 AND (W.CG_NO IS NOT NULL OR (W.CG_NO IS NULL AND R.CG_NO IS NOT NULL)) AND C.CG_NO NOT LIKE 'RTS' + '%'
AND C.VSL_CALL_ID = #{vslCallId}
AND C.SHIP_CALL_NO = #{scn}
AND C.OPE_CLASS_CD = #{category}
AND C.CNSNE LIKE '%' + #{cnsne} + '%'
AND C.CMDT_CD LIKE '%' + #{cmdtCd} + '%'
AND B.MF_DOC_ID = #{masterBlNo}
AND B.BL_NO = #{blNo}
AND S.MF_DOC_ID = #{bookingNo}
AND S.SHIPG_NOTE_NO = #{snNo}
AND (S.LOT_NO = #{lotNo} OR B.LOT_NO = #{lotNo})
AND W.WH_LOC_ID LIKE '%' + #{whLocId} + '%'
AND I.WH_LOC_TP LIKE '%' + #{whTpCd} + '%'
GROUP BY C.VSL_CALL_ID, P.VSL_NM, C.OPE_CLASS_CD, DBO.F_CM_CODE_NM ('MT', 'CATGTP', C.OPE_CLASS_CD), B.BL_NO, B.MF_DOC_ID, S.SHIPG_NOTE_NO, S.MF_DOC_ID, C.CG_NO, J.RCYN, J.WH_TP_CD, DBO.F_CM_CODE_NM ('MT', 'CGCOCD', J.WH_TP_CD), R.RC_CO_CD, DBO.F_CM_CODE_NM ('MT', 'RCCOCD', R.RC_CO_CD), ISNULL(B.CMDT_CD, S.CMDT_CD), DBO.F_GET_CMDT_DESC (ISNULL(B.CMDT_CD, S.CMDT_CD)), ISNULL(B.LOT_NO, S.LOT_NO), I.WH_LOC_TP, DBO.F_CM_CODE_NM ('MT', 'WHTP', I.WH_LOC_TP), ISNULL(I.WGT, 0), ISNULL(I.MSRMT, 0), ISNULL(I.PKGQTY, 0), B.SHPR, S.SHPR, B.CG_TP_CD, S.CG_TP_CD, B.PROJECT_CARGO, S.PROJECT_CARGO, B.CNSNE, S.CNSNE, R.RMK, I.LOC_ID, O.WGT, ORC.WGT, O.MSRMT, ORC.MSRMT, O.PKGQTY, ORC.PKGQTY, ISNULL(R.WGT, 0), ISNULL(R.MSRMT, 0), ISNULL(R.PKGQTY, 0), ISNULL(R.WGT, 0), ISNULL(R.MSRMT, 0), ISNULL(R.PKGQTY, 0), C.BL_NO, C.SHIPG_NOTE_NO )

mapper.select:
/*whReconciliation.selectWHRecnList*/
SELECT * FROM (SELECT inner_querry.*, ROW_NUMBER() OVER(ORDER BY (SELECT NULL)) row_num FROM getWarehouseReconcileItems AS inner_querry )inner_querry WHERE inner_querry.row_num > (CASE WHEN #{pageNo} = '0' THEN 1 ELSE CAST(#{pageNo} AS INT) END - 1) * CAST(#{sizePerPage} AS INT) AND inner_querry.row_num <= CASE WHEN #{pageNo} = '0' THEN 1 ELSE CAST(#{pageNo} AS INT) END * CAST(#{sizePerPage} AS INT)
SELECT * FROM getWarehouseReconcileItems

select.if:
SELECT BL.BL_NO DOCNO, BL.VSL_CALL_ID VSLCALLID FROM TMT_BL BL, TMT_CG_MST CM WHERE BL.VSL_CALL_ID = CM.VSL_CALL_ID AND BL.BL_NO = CM.BL_NO
AND BL.VSL_CALL_ID = #{vslCallId}
AND BL.VSL_CALL_ID <> 'NonCallId'
AND CM.FWR_AGNT = #{fwAgent}
AND CM.OPE_CLASS_CD = #{category}

SELECT SHIPG_NOTE_NO DOCNO, VSL_CALL_ID VSLCALLID FROM TMT_SHIPG_NOTE WHERE VSL_CALL_ID IS NOT NULL
AND VSL_CALL_ID = #{vslCallId}
AND VSL_CALL_ID <> 'NonCallId'
AND FWRD = #{fwAgent}
AND EST_ARRV_DT BETWEEN CONVERT(DATETIME, #{estArrvDtFrom}+' 00:00',103) AND CONVERT(DATETIME, #{estArrvDtTo}+' 23:59',103)

SELECT GR.GR_NO DOCNO , SN.VSL_CALL_ID VSLCALLID FROM TMT_SHIPG_NOTE SN, TMT_GR GR WHERE SN.VSL_CALL_ID IS NOT NULL
AND SN.VSL_CALL_ID = #{vslCallId}
AND SN.VSL_CALL_ID <> 'NonCallId'
AND SN.FWRD = #{fwAgent}
AND SN.SHIPG_NOTE_NO = #{snNo}
AND SN.EST_ARRV_DT BETWEEN CONVERT(DATETIME, #{estArrvDtFrom}+' 00:00',103) AND CONVERT(DATETIME, #{estArrvDtTo}+' 23:59',103)
AND SN.VSL_CALL_ID = GR.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO

mapper.select:
SELECT /*WHReconciliationMap.selectPivotList*/ OPECLASSCD, OPECLASSNM, VSLCALLID, CMDTCD, CMDTGRPCD, LOCID, SPCACOCD, SPCACONM, FWRAGNT, SHPR, SHPRNAME, CNSNE, CNSNENAME,
SUM (WGT) AS TOTAL
SUM (MSRMT) AS TOTAL
SUM (PKGQTY) AS TOTAL
SUM (FREIGHTTON) AS TOTAL
FROM ( SELECT C.OPE_CLASS_CD OPECLASSCD, DBO.F_CM_CODE_NM ('MT', 'CATGTP', C.OPE_CLASS_CD) OPECLASSNM, A.VSLCALLID VSLCALLID, B.SPCACOCD SPCACOCD, DBO.F_CM_CODE_NM ('MT', 'SPCACOCD', B.SPCACOCD) SPCACONM, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) BL.FWRD FROM tmt_bl BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) SN.FWRD FROM TMT_SHIPG_NOTE SN WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS FWRAGNT, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1)BL.CMDT_CD FROM tmt_bl BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) SN.CMDT_CD FROM TMT_SHIPG_NOTE SN WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS CMDTCD, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) BL.CMDT_GRP_CD FROM tmt_bl BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) SN.CMDT_GRP_CD FROM TMT_SHIPG_NOTE SN WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS CMDTGRPCD, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) BL.CG_TP_CD FROM tmt_bl BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) SN.CG_TP_CD FROM TMT_SHIPG_NOTE SN WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS CGTPCD, B.WGT WGT, B.MSRMT MSRMT, B.PKGQTY PKGQTY, GREATEST (B.WGT, B.MSRMT) AS FREIGHTTON, A.LOCID LOCID, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) SHPR FROM TMT_BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) SHPR FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS SHPR, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) SHPR_NM FROM TMT_BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) SHPR_NM FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS SHPRNAME, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) CNSNE FROM TMT_BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) CNSNE FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS CNSNE, CASE WHEN C.OPE_CLASS_CD = 'I' THEN (SELECT TOP(1) CNSNE_NM FROM TMT_BL WHERE BL_NO = B.CGNO AND VSL_CALL_ID = B.VSLCALLID ) ELSE (SELECT TOP(1) CNSNE_NM FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = (SELECT SHIPG_NOTE_NO FROM TMT_GR WHERE GR_NO = A.CGNO AND VSL_CALL_ID = B.VSLCALLID) ) END AS CNSNENAME FROM ( SELECT VSLCALLID, CGNO, WHTPCD, STRING_AGG(LOCID, ',') WITHIN GROUP (ORDER BY LOCID) AS LOCID FROM ( SELECT VSLCALLID, CGNO, WHTPCD, LOCID, ROW_NUMBER() OVER(PARTITION BY VSLCALLID, CGNO, WHTPCD ORDER BY LOCID) AS CNT FROM ( SELECT I.VSL_CALL_ID AS VSLCALLID, I.CG_NO AS CGNO, I.WH_TP_CD AS WHTPCD, I.WH_LOC_ID AS LOCID, SUM(I.CG_WGT) AS WGT, SUM(I.CG_VOL) AS MSRMT, SUM(I.PKG_QTY) AS PKGQTY FROM TMT_INV_LOC I INNER JOIN TMT_JOB J ON I.JOB_NO = J.JOB_NO WHERE (I.CG_WGT <> 0 OR I.CG_VOL <> 0 OR I.PKG_QTY <> 0) GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_TP_CD, I.WH_LOC_ID ) AS InnerQuery ) AS t GROUP BY VSLCALLID, CGNO, WHTPCD) A, (SELECT VSLCALLID, CGNO, WHTPCD, WGT, MSRMT, PKGQTY, YARDINDT, LORRYNO, ISNULL( (SELECT MAX (SP_CA_CO_CD) FROM TMT_JOB WHERE VSL_CALL_ID = VSLCALLID AND CG_NO = CGNO AND JOB_CO_CD = WHTPCD AND SP_CA_CO_CD IS NOT NULL), ' ') SPCACOCD, ISNULL( (SELECT MAX ('Y') FROM TMT_JOB WHERE VSL_CALL_ID = VSLCALLID AND CG_NO = CGNO AND JOB_CO_CD = WHTPCD AND JOB_TP_CD = 'RC'), 'N') RCYN FROM ( SELECT I.VSL_CALL_ID VSLCALLID, I.CG_NO CGNO, I.WH_TP_CD WHTPCD, MIN (J.WORK_END_DT) YARDINDT, MAX (J.LORRY_NO) LORRYNO, SUM (I.CG_WGT) WGT, SUM (I.CG_VOL) MSRMT, SUM (I.PKG_QTY) PKGQTY FROM TMT_INV_LOC I, TMT_JOB J WHERE 1 = 1 AND I.JOB_NO = J.JOB_NO GROUP BY I.VSL_CALL_ID, I.CG_NO, I.WH_TP_CD ) sub WHERE (WGT <> 0 OR MSRMT <> 0 OR PKGQTY <> 0)) B, TMT_CG_MST C WHERE A.VSLCALLID = B.VSLCALLID AND A.CGNO = B.CGNO AND A.WHTPCD = B.WHTPCD AND A.VSLCALLID = C.VSL_CALL_ID AND A.CGNO = C.CG_NO ) T WHERE 1=1
AND T.OPECLASSCD = #{category}
AND T.FWRAGNT = #{fwAgent}
AND T.CNSNE = #{cnsne}
AND T.SHPR = #{shpr}
AND T.VSLCALLID = #{vslCallId}
AND T.PKGNO like '%' + #{pkgNo} + '%'
AND T.CMDTCD = #{cmdtCd}
AND T.CMDTGRPCD = #{cmdtGrpCd}
AND T.YARDINDT > CONVERT(DATE, #{fromDt}, 103)
AND T.YARDINDT < CONVERT(DATE, #{toDt}, 103)
GROUP BY OPECLASSCD, OPECLASSNM, VSLCALLID, CMDTCD, CMDTGRPCD, LOCID, SPCACOCD, SPCACONM, FWRAGNT, SHPR, SHPRNAME, CNSNE, CNSNENAME
