mapper.sql:
SELECT /* cargoMaster.getCargoSearch */ DIV, OPECLASSCD, VSLCALLID, BLSN, CGNO, SHIPGAGNT, FWRAGNT, dbo.F_CM_CODE_NM('MT', 'DELVTP', DELVTPCD) AS DELVTPCD, CMDTCD, ISNULL(GRSMT,0) AS GRSMT, ISNULL(GRSQTY,0) AS GRSQTY, ISNULL(DIRMT,0) AS DIRMT, ISNULL(DIRQTY,0) AS DIRQTY, ISNULL(INDMT,0) AS INDMT, ISNULL(INDQTY,0) AS INDQTY, ISNULL(NORMT,0) AS NORMT, ISNULL(NORQTY,0) AS NORQTY, ISNULL(DMGMT,0) AS DMGMT, ISNULL(DMGQTY,0) AS DMGQTY, ISNULL(SHUMT,0) AS SHUMT, ISNULL(SHUQTY,0) AS SHUQTY, ISNULL(GPMT,0) AS GPMT, ISNULL(GPQTY,0) AS GPQTY, C.ATA,
FROM (
UNION ALL
) A, TMT_VSL_PART B, TMT_VSL_SCH C WHERE A.DIV IN ('SN','GR','BL') AND C.VSL_CALL_ID = A.VSLCALLID AND C.VSL_CD = B.VSL_CD AND (A.OPECLASSCD IS NOT NULL AND A.OPECLASSCD <> '')
AND B.SA_CORP_ID = #{ptnrCode} AND SHIPGAGNT = #{ptnrCode}
AND FWRAGNT = #{ptnrCode}
AND A.BLSN = #{blNo}
AND A.BLSN = #{shipgNoteNo}
AND A.DELVTPCD = #{delvTpCd}
AND A.CGNO = #{grNo}
AND A.OPECLASSCD = #{opeClassCd}
AND C.ATA BETWEEN CONVERT(DATE, #{fromDate}, 'dd/MM/yyyy') AND CONVERT(DATE, #{toDate}, 'dd/MM/yyyy')

mapper.select:
SELECT /* cargoMaster.selectCargoDoOperation */ VSL_CALL_ID AS VSLCALLID, CG_NO AS CGNO, STAT_CD AS STATCD FROM TMT_CG_MST WHERE VSL_CALL_ID = #{vslCallId}
AND SHIPG_NOTE_NO = #{shipgNoteNo}
AND BL_NO = #{blNo} AND STAT_CD IN ('OV', 'DV')

SELECT /* cargoMaster.selectGrGoComboList */ S.VSL_CALL_ID AS VSLCALLID, G.SHIPG_NOTE_NO AS SHIPGNOTENO, S.BERTH_LOC AS BERTHLOC, FORMAT(S.ETA, 'dd/MM/yyyy HH:mm') AS ETA, FORMAT(S.ETD, 'dd/MM/yyyy HH:mm') AS ETD, (SELECT FORMAT(MAX(EST_ARRV_DT), 'dd/MM/yyyy HH:mm') FROM TMT_SHIPG_NOTE WHERE SHIPG_NOTE_NO = G.SHIPG_NOTE_NO) AS ESTARRVDT FROM TMT_GR G, TMT_VSL_SCH S WHERE G.VSL_CALL_ID = S.VSL_CALL_ID
AND G.VSL_CALL_ID = #{vslCallId}
AND G.GR_NO = #{grNo}
AND G.GR_NO = #{cgNo}

SELECT TOP(1) /* cargoMaster.selectGpGoComboList */ S.VSL_CALL_ID AS VSLCALLID, B.BL_NO AS BLNO, S.BERTH_LOC AS BERTHLOC, FORMAT(S.ETA, 'dd/MM/yyyy HH:mm') AS ETA, FORMAT(S.ETD, 'dd/MM/yyyy HH:mm') AS ETD FROM TMT_VSL_SCH S, TMT_BL B, TMT_CG_ARRV_DELV D WHERE 1 = 1
AND S.VSL_CALL_ID = #{vslCallId}
AND S.VSL_CALL_ID = D.VSL_CALL_ID AND S.VSL_CALL_ID = B.VSL_CALL_ID
AND D.GATE_PASS_NO = #{gatePassNo}

SELECT /* cargoMaster.selectIsCargoAvDvChk */
	'1' 
FROM 
	TMT_CG_ARRV_DELV
WHERE 
	1 = 1
	AND VSL_CALL_ID 									= #{vslCallId} 
	AND CG_NO 											= #{cgNo}
	AND LORRY_NO 										= #{lorryNo}

		AND CG_IN_OUT_CD 								= #{cgInOutCd}


		AND GATE_TXN_NO 								= #{gateTxnNo}

	AND (GATE_PASS_NO IS NULL							OR GATE_PASS_NO = '')
	AND (GATE_OUT_DT IS NULL							OR GATE_OUT_DT = '')

mapper.sql:
SELECT /* cargoMaster.cargoSearchExportGR */ 'GR' AS DIV, CM.OPE_CLASS_CD AS OPECLASSCD, GR.VSL_CALL_ID AS VSLCALLID, SN.SHIPG_NOTE_NO AS BLSN, GR.GR_NO AS CGNO, SN.SHIPG_AGNCY AS SHIPGAGNT, SN.FWRD AS FWRAGNT, SN.DELV_TP_CD AS DELVTPCD, SN.CMDT_CD AS CMDTCD, ISNULL(CM.PKG_QTY, 0) + dbo.F_GET_RC_PKG_QTY( CM.VSL_CALL_ID , CM.CG_NO ) AS GRSQTY, ISNULL(CM.CG_WGT,0) + dbo.F_GET_RC_WGT(CM.VSL_CALL_ID, CM.CG_NO) AS GRSMT, VS2.DIR_MT AS DIRMT, VS2.DIR_QTY AS DIRQTY, VS2.IND_MT AS INDMT, VS2.IND_QTY AS INDQTY, WH.NOR_MT AS NORMT, WH.NOR_QTY AS NORQTY, WH.DMG_MT AS DMGMT, WH.DMG_QTY AS DMGQTY, WH.SHU_MT AS SHUMT, WH.SHU_QTY AS SHUQTY, GP.GP_MT AS GPMT, GP.GP_QTY AS GPQTY FROM TMT_SHIPG_NOTE SN, TMT_CG_MST CM, TMT_GR GR LEFT OUTER JOIN (SELECT VSL_CALL_ID AS VSL_CALL_ID, CG_NO AS CG_NO, SUM(ISNULL(CG_WGT,0)) AS GRS_MT, SUM(ISNULL(PKG_QTY,0)) AS GRS_QTY, SUM( CASE DELV_TP_CD WHEN 'D' THEN ISNULL(CG_WGT, 0) ELSE 0 END ) AS DIR_MT, SUM( CASE DELV_TP_CD WHEN 'D' THEN ISNULL(PKG_QTY, 0) ELSE 0 END ) AS DIR_QTY, SUM( CASE DELV_TP_CD WHEN 'I' THEN ISNULL(CG_WGT, 0) ELSE 0 END ) AS IND_MT, SUM( CASE DELV_TP_CD WHEN 'I' THEN ISNULL(PKG_QTY,0) ELSE 0 END ) AS IND_QTY FROM TMT_JOB WHERE 1 = 1
AND VSL_CALL_ID = #{vslCallId}
AND JOB_TP_CD IN ('LD','DS') AND JOB_PURP_CD IN ('GV', 'AV') GROUP BY VSL_CALL_ID, CG_NO ) VS2 ON GR.VSL_CALL_ID = VS2.VSL_CALL_ID AND GR.GR_NO = VS2.CG_NO LEFT OUTER JOIN (SELECT TJ.VSL_CALL_ID AS VSL_CALL_ID, TJ.CG_NO AS CG_NO, SUM( CASE TL.WH_TP_CD WHEN 'G' THEN TL.CG_WGT ELSE 0 END ) AS NOR_MT, SUM( CASE TL.WH_TP_CD WHEN 'G' THEN TL.PKG_QTY ELSE 0 END ) AS NOR_QTY, SUM( CASE TL.WH_TP_CD WHEN 'D' THEN TL.CG_WGT ELSE 0 END ) AS DMG_MT, SUM( CASE TL.WH_TP_CD WHEN 'D' THEN TL.PKG_QTY ELSE 0 END ) AS DMG_QTY, SUM( CASE TL.WH_TP_CD WHEN 'S' THEN TL.CG_WGT ELSE 0 END ) AS SHU_MT, SUM( CASE TL.WH_TP_CD WHEN 'S' THEN TL.PKG_QTY ELSE 0 END ) AS SHU_QTY FROM TMT_JOB TJ, TMT_INV_LOC TL WHERE TJ.JOB_NO = TL.JOB_NO
AND TJ.VSL_CALL_ID = #{vslCallId}
AND (TJ.SP_CA_CO_CD IS NULL OR TJ.SP_CA_CO_CD = '') GROUP BY TJ.VSL_CALL_ID, TJ.CG_NO ) WH ON GR.VSL_CALL_ID = WH.VSL_CALL_ID AND GR.GR_NO = WH.CG_NO LEFT OUTER JOIN (SELECT VSL_CALL_ID, CG_IN_OUT_CD, CG_NO, SUM(ISNULL(CG_WGT,0)) AS GP_MT,
SUM(ISNULL(PKG_QTY,0)) AS GP_QTY FROM TMT_CG_ARRV_DELV WHERE 1 = 1
AND VSL_CALL_ID = #{vslCallId}
AND (GATE_PASS_NO IS NOT NULL AND GATE_PASS_NO <> '') GROUP BY VSL_CALL_ID, CG_IN_OUT_CD, CG_NO ) AS GP ON GR.VSL_CALL_ID = GP.VSL_CALL_ID AND GR.GR_NO = GP.CG_NO WHERE SN.VSL_CALL_ID = GR.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO AND SN.VSL_CALL_ID = CM.VSL_CALL_ID AND GR.GR_NO = CM.CG_NO
AND GR.VSL_CALL_ID = #{vslCallId}

SELECT /* cargoMaster.cargoSearchExportSN */ 'SN' AS DIV, CM.OPE_CLASS_CD AS OPECLASSCD, SN.VSL_CALL_ID AS VSLCALLID, SN.SHIPG_NOTE_NO AS BLSN, '' AS CGNO, SN.SHIPG_AGNCY AS SHIPGAGNT, SN.FWRD AS FWRAGNT, SN.DELV_TP_CD AS DELVTPCD, SN.CMDT_CD AS CMDTCD, CM.GRS_MT AS GRSMT, CM.GRS_QTY AS GRSQTY, VS2.DIR_MT AS DIRMT, VS2.DIR_QTY AS DIRQTY, VS2.IND_MT AS INDMT, VS2.IND_QTY AS INDQTY, WH.NOR_MT AS NORMT, WH.NOR_QTY AS NORQTY, WH.DMG_MT AS DMGMT, WH.DMG_QTY AS DMGQTY, WH.SHU_MT AS SHUMT, WH.SHU_QTY AS SHUQTY, GP.GP_MT AS GPMT, GP.GP_QTY AS GPQTY
FROM (SELECT TC.VSL_CALL_ID AS VSL_CALL_ID, SN.SHIPG_NOTE_NO AS CG_NO, SUM( ISNULL(TC.PKG_QTY,0) + dbo.F_GET_RC_PKG_QTY( TC.VSL_CALL_ID, TC.CG_NO ) ) AS GRS_QTY, SUM( ISNULL(TC.CG_WGT,0) + dbo.F_GET_RC_WGT( TC.VSL_CALL_ID, TC.CG_NO ) ) AS GRS_MT,
TC.OPE_CLASS_CD FROM TMT_CG_MST TC, TMT_SHIPG_NOTE SN WHERE 1 = 1
AND TC.VSL_CALL_ID = #{vslCallId}
AND TC.VSL_CALL_ID = SN.VSL_CALL_ID AND TC.SHIPG_NOTE_NO = SN.SHIPG_NOTE_NO GROUP BY TC.VSL_CALL_ID, SN.SHIPG_NOTE_NO, TC.OPE_CLASS_CD ) CM, TMT_SHIPG_NOTE SN LEFT OUTER JOIN (SELECT TC.VSL_CALL_ID AS VSL_CALL_ID, TC.SHIPG_NOTE_NO AS CG_NO, SUM(ISNULL(TJ.CG_WGT,0)) AS GRS_MT, SUM(ISNULL(TJ.PKG_QTY,0)) AS GRS_QTY, SUM( CASE TJ.DELV_TP_CD WHEN 'D' THEN ISNULL(TJ.CG_WGT, 0) ELSE 0 END ) AS DIR_MT, SUM( CASE TJ.DELV_TP_CD WHEN 'D' THEN ISNULL(TJ.PKG_QTY, 0) ELSE 0 END ) AS DIR_QTY, SUM( CASE TJ.DELV_TP_CD WHEN 'I' THEN ISNULL(TJ.CG_WGT, 0) ELSE 0 END ) AS IND_MT, SUM( CASE TJ.DELV_TP_CD WHEN 'I' THEN ISNULL(TJ.PKG_QTY, 0) ELSE 0 END ) AS IND_QTY
FROM TMT_CG_MST TC, TMT_JOB TJ WHERE 1 = 1
AND TJ.VSL_CALL_ID = #{vslCallId}
AND TJ.JOB_TP_CD IN ('LD','DS') AND TJ.JOB_PURP_CD IN ('GV', 'AV') AND TC.VSL_CALL_ID = TJ.VSL_CALL_ID AND TC.CG_NO = TJ.CG_NO AND (TC.SHIPG_NOTE_NO IS NOT NULL AND TC.SHIPG_NOTE_NO <> '') GROUP BY TC.VSL_CALL_ID, TC.SHIPG_NOTE_NO ) VS2 ON SN.VSL_CALL_ID = VS2.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = VS2.CG_NO LEFT OUTER JOIN (SELECT TC.VSL_CALL_ID AS VSL_CALL_ID, TC.SHIPG_NOTE_NO AS CG_NO, SUM( CASE TL.WH_TP_CD WHEN 'G' THEN TL.CG_WGT ELSE 0 END ) AS NOR_MT, SUM( CASE TL.WH_TP_CD WHEN 'G' THEN TL.PKG_QTY ELSE 0 END ) AS NOR_QTY, SUM( CASE TL.WH_TP_CD WHEN 'D_LR_M3' THEN TL.CG_WGT ELSE 0 END ) AS DMG_MT, SUM( CASE TL.WH_TP_CD WHEN 'D' THEN TL.PKG_QTY ELSE 0 END ) AS DMG_QTY, SUM( CASE TL.WH_TP_CD WHEN 'S' THEN TL.CG_WGT ELSE 0 END ) AS SHU_MT, SUM( CASE TL.WH_TP_CD WHEN 'S' THEN TL.PKG_QTY ELSE 0 END ) AS SHU_QTY
FROM TMT_CG_MST TC, TMT_JOB TJ, TMT_INV_LOC TL WHERE TC.VSL_CALL_ID = TJ.VSL_CALL_ID AND TC.CG_NO = TJ.CG_NO AND TJ.JOB_NO = TL.JOB_NO
AND TJ.VSL_CALL_ID = #{vslCallId}
AND (TJ.SP_CA_CO_CD IS NULL OR TJ.SP_CA_CO_CD = '') AND (TC.SHIPG_NOTE_NO IS NOT NULL AND TC.SHIPG_NOTE_NO <> '') GROUP BY TC.VSL_CALL_ID, TC.SHIPG_NOTE_NO ) WH ON SN.VSL_CALL_ID = WH.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = WH.CG_NO LEFT OUTER JOIN (SELECT TC.VSL_CALL_ID AS VSL_CALL_ID, TC.SHIPG_NOTE_NO AS CG_NO, SUM(ISNULL(TA.CG_WGT,0)) AS GP_MT,
SUM(ISNULL(TA.PKG_QTY,0)) AS GP_QTY FROM TMT_CG_MST TC, TMT_CG_ARRV_DELV TA WHERE TC.VSL_CALL_ID = TA.VSL_CALL_ID AND TC.CG_NO = TA.CG_NO
AND TA.VSL_CALL_ID = #{vslCallId}
AND (TA.GATE_PASS_NO IS NOT NULL AND TA.GATE_PASS_NO <> '') AND (TC.SHIPG_NOTE_NO IS NOT NULL AND TC.SHIPG_NOTE_NO <> '') GROUP BY TC.VSL_CALL_ID, TC.SHIPG_NOTE_NO ) GP ON SN.VSL_CALL_ID = GP.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = GP.CG_NO WHERE 1 = 1 AND SN.SHIPG_NOTE_NO = CM.CG_NO
AND SN.VSL_CALL_ID = #{vslCallId}

mapper.select:
SELECT /* cargoMaster.selectWHComboList */ LOC_ID AS LOCID, LOC_NM AS LOCNM FROM TMT_LOC_DEF WHERE 1 = 1
AND LOC_DIV_CD = #{locDivCd}
ORDER BY LOC_NM
