SELECT DISTINCT /*spaceMovementPlan.getSelectSpaceMoveMentPlanDetail*/ SN.VSL_CALL_ID AS VSLCALLID, SN.VSL_CD AS VSLCD, SN.CALL_YEAR AS CALLYEAR, SN.CALL_SEQ AS CALLSEQ, REQ.REQ_NO AS REQNO, REQ.SEQ AS SEQ, REQ.SEQ AS REQSEQ, SN.MF_DOC_ID AS MFDOCID, NULL AS BLNO, NULL AS DONO, CASE SN.CG_TP_CD WHEN 'RCV' THEN GR.GR_NO ELSE '' END AS GRNO, CASE SN.CG_TP_CD WHEN 'RCV' THEN GR.GR_NO ELSE '' END AS SDOGRNO,
(SELECT COUNT(R.CHASSIS_NO) FROM TMT_RORO_MST R WHERE R.VSL_CALL_ID = SN.VSL_CALL_ID AND R.CG_NO = SN.SHIPG_NOTE_NO AND R.GR_NO = GR.GR_NO AND R.IX_CD = 'X' ) AS NOOFVIN, SN.SHIPG_NOTE_NO AS SHIPGNOTENO, SN.SHIPG_NOTE_NO AS BLSN, REQ_POS AS REQPOS, PLAN_LOC_ID AS PLANLOCID, PREV_LOC_ID AS PREVLOCID, SN.MARK_NO AS PKGNO, SN.GDS_RMK AS PKGMARK, CASE SN.CG_TP_CD WHEN 'RCV' THEN GR.CG_WGT ELSE SN.CG_WGT END AS REQWGT, CASE SN.CG_TP_CD WHEN 'RCV' THEN GR.CG_VOL ELSE SN.CG_VOL END AS REQMSRMT, CASE SN.CG_TP_CD WHEN 'RCV' THEN GR.PKG_QTY ELSE SN.PKG_QTY END AS REQQTY, SN.CATG_CD AS CATGCD, (SELECT TOP 1 S_CD_NM FROM TMT_CD_MSTD MSTD WHERE L_CD = 'MT' AND M_CD = 'CATGTP' AND SN.CATG_CD = MSTD.S_CD ) AS CATGNM, SN.CG_TP_CD AS CGTPCD, (SELECT TOP 1 S_CD_NM FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'CGTP' AND S_CD_USE = 'Y' AND S_CD = SN.CG_TP_CD ) AS CGTPNM, SN.CMDT_GRP_CD AS CMDTGRPCD, (SELECT TOP 1 CMDT_GRP_DESC FROM TMT_CMDT_GRP WHERE CMDT_GRP_CD = SN.CMDT_GRP_CD) AS CMDTGRPNM, SN.CMDT_CD AS CMDTCD, (SELECT TOP 1 CMDT_DESC FROM TMT_CMDT CMDT WHERE CMDT.CMDT_CD = SN.CMDT_CD AND CMDT.CMDT_GRP_CD = SN.CMDT_GRP_CD ) AS CMDTNM, SN.PKG_TP_CD AS PKGTPCD, (SELECT TOP 1 S_CD_NM FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'PKGTP' AND S_CD_USE = 'Y' AND S_CD = SN.PKG_TP_CD ) AS PKGTPNM, SN.IMDG AS IMDGCLASS, SN.UNNO AS UNNO, PAYER AS PAYER, REQ_TP_CD AS REQTPCD, MV_TP AS MVTP, (CASE REQ_TP_CD WHEN 'SPA' THEN 'Space' WHEN 'MOV' THEN 'Movement' ELSE 'Space' END) AS REQTPNM, FORMAT(REQ_DT,'dd/MM/yyyy HH:mm') AS REQDT, FORMAT(PLAN_DT,'dd/MM/yyyy HH:mm') AS PLANDT, REQR AS REQR, CG_REF_NO AS CGREFNO, ISNULL(REQ.RMK,' ') AS RMK, REQ.STAT_CD AS STATCD, (CASE REQ.STAT_CD WHEN 'SAV' THEN 'Saved' WHEN 'REQ' THEN 'Requested' WHEN 'CNF' THEN 'Planned' WHEN 'REJ' THEN 'Rejected' ELSE 'Saved' END) AS STATNM, REQ.OPE_CLASS_CD AS OPECLASSCD, REQ.REF_NO AS REFNO, CASE WHEN REQ.ETA IS NULL OR REQ.ETA = '' THEN CONVERT(DATE, SN.EST_ARRV_DT) ELSE REQ.ETA END AS ETA, SVC_DT AS SVCDT, PERIOD AS PERIOD, REQ_M2 AS REQM2, REQ_MT AS REQMT, PLAN_LOCS AS LOCID, SN.FWRD AS FWDAGNT, SN.SHIPG_AGNCY AS SHIPGAGNT, SN.SHPR AS CNGSHP, (CASE WHEN (REQ_NO IS NOT NULL AND REQ_NO <> '') THEN 'U' ELSE 'C' END) AS WORKINGSTATUS, SN.LOT_NO AS LOTNO FROM TMT_SHIPG_NOTE SN LEFT OUTER JOIN TMT_GR GR ON SN.VSL_CALL_ID = GR.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = GR.SHIPG_NOTE_NO LEFT OUTER JOIN TMT_SPC_REQ REQ ON (SN.VSL_CALL_ID = REQ.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = REQ.SHIPG_NOTE_NO AND GR.GR_NO = REQ.GR_NO
) INNER JOIN TMT_SHIPG_NOTE_DTL R ON SN.VSL_CALL_ID = R.VSL_CALL_ID AND SN.SHIPG_NOTE_NO = R.SHIPG_NOTE_NO LEFT OUTER JOIN ( SELECT VSL_CALL_ID, TMT_SPC_PLAN.REQ_SEQ, STRING_AGG(LOC_ID, ',') WITHIN GROUP (ORDER BY LOC_ID) AS PLAN_LOCS FROM TMT_SPC_PLAN GROUP BY VSL_CALL_ID, TMT_SPC_PLAN.REQ_SEQ ) B ON ( REQ.VSL_CALL_ID = B.VSL_CALL_ID AND REQ.SEQ = B.REQ_SEQ ) WHERE 1 = 1
AND SN.VSL_CALL_ID = #{vslCallId}
AND SN.MF_DOC_ID = #{bookingNo}
AND SN.SHIPG_NOTE_NO = #{shipgNoteNo}
AND SN.CMDT_GRP_CD = #{cmdtGrpCd}
AND SN.SHPR = #{cngShp}
AND SN.POD = #{pod}
AND (REQ_NO IS NULL OR REQ_NO = '')
AND (REQ_NO IS NOT NULL AND REQ_NO <> '')
AND REQ_TP_CD = #{reqTpCd}
AND
REQ_DT >= CONVERT(DATE, #{reqStDt}, 103)
AND
REQ_DT <= DATEADD(DAY, 1, CONVERT(DATE, #{reqEndDt}, 103))
AND PLAN_LOC_ID LIKE '${reqPos}%'
AND SN.LOT_NO = #{lotNo}
AND GR.GR_NO = #{grNo}

SELECT DISTINCT /*spaceMovementPlan.getSelectSpaceMoveMentPlanDetail*/ BL.VSL_CALL_ID AS VSLCALLID, BL.VSL_CD AS VSLCD, BL.CALL_YEAR AS CALLYEAR, BL.CALL_SEQ AS CALLSEQ, REQ_NO AS REQNO, REQ.SEQ AS SEQ, REQ.SEQ AS REQSEQ, BL.MF_DOC_ID AS MFDOCID, BL.BL_NO AS BLNO, DO.DO_NO AS DONO, NULL AS GRNO, CASE BL.CG_TP_CD WHEN 'RCV' THEN S.SDO_NO ELSE '' END AS SDOGRNO, CASE BL.CG_TP_CD WHEN 'RCV' THEN S.TOTAL_VIN ELSE 0 END AS NOOFVIN, NULL AS SHIPGNOTENO, BL.BL_NO AS BLSN, REQ_POS AS REQPOS, PLAN_LOC_ID AS PLANLOCID, PREV_LOC_ID AS PREVLOCID, BL.MARK_NO AS PKGNO, BL.GDS_RMK AS PKGMARK, CASE BL.CG_TP_CD WHEN 'RCV' THEN S.CG_WGT ELSE BL.CG_WGT END AS REQWGT, CASE BL.CG_TP_CD WHEN 'RCV' THEN S.CG_VOL ELSE BL.CG_VOL END AS REQMSRMT, CASE BL.CG_TP_CD WHEN 'RCV' THEN S.PKG_QTY ELSE BL.PKG_QTY END AS REQQTY, BL.CATG_CD AS CATGCD, (SELECT TOP 1 S_CD_NM FROM TMT_CD_MSTD MSTD WHERE L_CD = 'MT' AND M_CD = 'CATGTP' AND BL.CATG_CD = MSTD.S_CD ) AS CATGNM, BL.CG_TP_CD AS CGTPCD, (SELECT TOP 1 S_CD_NM FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'CGTP' AND S_CD_USE = 'Y' AND S_CD = BL.CG_TP_CD ) AS CGTPNM, BL.CMDT_GRP_CD AS CMDTGRPCD, (SELECT TOP 1 CMDT_GRP_DESC FROM TMT_CMDT_GRP WHERE CMDT_GRP_CD = BL.CMDT_GRP_CD) AS CMDTGRPNM, BL.CMDT_CD AS CMDTCD, (SELECT TOP 1 CMDT_DESC FROM TMT_CMDT CMDT WHERE CMDT.CMDT_CD = BL.CMDT_CD ) AS CMDTNM, BL.PKG_TP_CD AS PKGTPCD, (SELECT TOP 1 S_CD_NM FROM TMT_CD_MSTD WHERE L_CD = 'MT' AND M_CD = 'PKGTP' AND S_CD_USE = 'Y' AND S_CD = BL.PKG_TP_CD ) AS PKGTPNM, BL.IMDG_CLASS AS IMDGCLASS, BL.UNNO AS UNNO, PAYER AS PAYER, REQ_TP_CD AS REQTPCD, MV_TP AS MVTP, (CASE REQ_TP_CD WHEN 'SPA' THEN 'Space' WHEN 'MOV' THEN 'Movement' ELSE 'Space' END) AS REQTPNM, FORMAT(REQ_DT,'dd/MM/yyyy HH:mm') AS REQDT, FORMAT(PLAN_DT,'dd/MM/yyyy HH:mm') AS PLANDT, REQR AS REQR, CG_REF_NO AS CGREFNO, ISNULL(REQ.RMK,' ') AS RMK, REQ.STAT_CD AS STATCD, (CASE REQ.STAT_CD WHEN 'SAV' THEN 'Saved' WHEN 'REQ' THEN 'Requested' WHEN 'CNF' THEN 'Planned' WHEN 'REJ' THEN 'Rejected' ELSE 'Saved' END) AS STATNM, REQ.OPE_CLASS_CD AS OPECLASSCD, REQ.REF_NO AS REFNO, CASE WHEN REQ.ETA IS NULL OR REQ.ETA = '' THEN CONVERT(DATE, S.EST_ARRV_DT) ELSE REQ.ETA END AS ETA, SVC_DT AS SVCDT, PERIOD AS PERIOD, REQ_M2 AS REQM2, REQ_MT AS REQMT, PLAN_LOCS AS LOCID, BL.FWRD AS FWDAGNT, (SELECT AGENCY_CODE FROM TMT_AGENCY_INFO PTNR INNER JOIN (SELECT VSL_CALL_ID, ARRV_SA_ID FROM TMT_VSL_SCH ) SCH ON SCH.ARRV_SA_ID = PTNR.AGENCY_CODE WHERE SCH.VSL_CALL_ID = BL.VSL_CALL_ID ) AS SHIPGAGNT, BL.CNSNE AS CNGSHP, (CASE WHEN (REQ_NO IS NOT NULL AND REQ_NO <> '') THEN 'U' ELSE 'C' END) AS WORKINGSTATUS, BL.LOT_NO AS LOTNO FROM TMT_BL BL LEFT OUTER JOIN TMT_DO DO ON (BL.VSL_CALL_ID = DO.VSL_CALL_ID AND BL.BL_NO = DO.BL_NO) LEFT OUTER JOIN (SELECT SUM(ISNULL(DODT.I_QTY, 0) + ISNULL(DODT.D_QTY, 0)) AS TOTAL_VIN, SUM(ISNULL(DODT.I_QTY, 0) + ISNULL(DODT.D_QTY, 0)) AS PKG_QTY, SUM(ISNULL(DODT.I_MT, 0) + ISNULL(DODT.D_MT, 0)) AS CG_WGT, SUM(ISNULL(DODT.I_M3, 0) + ISNULL(DODT.D_M3, 0)) AS CG_VOL, DODT.VSL_CALL_ID, DODT.BL_NO, DODT.DO_NO, DODT.SDO_NO, DODT.EST_ARRV_DT AS EST_ARRV_DT FROM TMT_DO_DTL DODT WHERE DODT.VSL_CALL_ID = #{vslCallId} GROUP BY DODT.VSL_CALL_ID, DODT.BL_NO, DODT.DO_NO, DODT.SDO_NO, DODT.EST_ARRV_DT ) S ON BL.VSL_CALL_ID = S.VSL_CALL_ID AND BL.BL_NO = S.BL_NO AND DO.DO_NO = S.DO_NO LEFT OUTER JOIN TMT_SPC_REQ REQ ON (BL.VSL_CALL_ID = REQ.VSL_CALL_ID AND BL.BL_NO = REQ.BL_NO
AND S.SDO_NO = REQ.SDO_NO ) LEFT OUTER JOIN ( SELECT VSL_CALL_ID, TMT_SPC_PLAN.REQ_SEQ, STRING_AGG(LOC_ID, ',') WITHIN GROUP (ORDER BY LOC_ID) AS PLAN_LOCS FROM TMT_SPC_PLAN GROUP BY VSL_CALL_ID, TMT_SPC_PLAN.REQ_SEQ ) B ON (REQ.VSL_CALL_ID = B.VSL_CALL_ID AND REQ.SEQ = B.REQ_SEQ) WHERE 1 = 1
AND BL.VSL_CALL_ID = #{vslCallId}
