mapper.sql:
SELECT /*spaceMovementPlan.getSpcMovRequestGroupList*/ * FROM ( SELECT VSLCALLID AS VSLCALLID, SPC.REQNO AS REQNO, SPC.PAYER AS PAYER, (CASE CHARINDEX('MOV', STATUS) WHEN 0 THEN 'SPC' ELSE ( CASE CHARINDEX('SPC', STATUS) WHEN 0 THEN 'MOV' ELSE 'BSM' END) END ) AS REQTPCD, SPC.REQDT AS REQDT, SPC.STATNM AS STATNM, (CASE CHARINDEX('MOV', STATUS) WHEN 0 THEN 'Space' ELSE ( CASE CHARINDEX('SPC', STATUS) WHEN 0 THEN 'Movement' ELSE 'Both' END) END) AS REQTPNM, SPC.REQR AS REQR, SPC.STATCD AS STATCD, SPC.CGREFNO AS CGREFNO, SPC.PLANLOCID AS PLANLOCID, SPC.BLNO AS BLNO, SPC.SHIPGNOTENO AS SHIPGNOTENO, ISNULL( (SELECT TOP 1 LOT_NO FROM TMT_BL BL WHERE SPC.VSLCALLID = BL.VSL_CALL_ID AND SPC.BLNO = BL.BL_NO), (SELECT TOP 1 LOT_NO FROM TMT_SHIPG_NOTE SN WHERE SPC.VSLCALLID = SN.VSL_CALL_ID AND SPC.SHIPGNOTENO = SN.SHIPG_NOTE_NO) ) AS LOTNO, SPC.REQPOS, SPC.MFDOCID FROM ( SELECT VSL_CALL_ID AS VSLCALLID, BL_NO AS BLNO, SHIPG_NOTE_NO AS SHIPGNOTENO, MF_DOC_ID AS MFDOCID, REQ_NO AS REQNO, MAX(payer) AS PAYER, FORMAT(MAX(REQ_DT),'dd/MM/yyyy HH:mm') AS REQDT, MAX(REQR) AS REQR, MIN(STAT_CD) AS STATCD, (CASE MIN(STAT_CD) WHEN 'SAV' THEN 'Saved' WHEN 'REQ' THEN 'Requested' WHEN 'CNF' THEN 'Planned' WHEN 'REJ' THEN 'Rejected' ELSE 'Saved' END) AS STATNM, STRING_AGG(REQ_TP_CD, ',') WITHIN GROUP (ORDER BY REQ_TP_CD) AS STATUS, COUNT(SEQ) AS CGREFNO, REQ_POS AS REQPOS, PLAN_LOC_ID AS PLANLOCID FROM TMT_SPC_REQ GROUP BY VSL_CALL_ID, REQ_NO, REQ_POS, PLAN_LOC_ID, BL_NO, SHIPG_NOTE_NO, MF_DOC_ID ) SPC WHERE 1 = 1
AND VSLCALLID = #{vslCallId}
AND VSLCALLID <> 'STRG'
AND REQPOS LIKE '${reqPos}%'
AND PLANLOCID = #{planLocId}
) AS S WHERE 1 = 1
AND
CONVERT(DATETIME, REQDT,103) >= CONVERT(DATETIME, #{reqStDt},103)
AND
CONVERT(DATETIME, REQDT, 103) <= DATEADD(DAY, 1, CONVERT(DATETIME, #{reqEndDt}, 103))
AND REQTPCD = #{reqTpCd}
AND STATCD = #{statCd}
AND LOTNO = #{lotNo}

mapper.select:
SELECT /*spaceMovementPlan.selectBlSnInfo*/ VSL_CALL_ID AS VSLCALLID, SHIPG_AGNT AS SHIPGAGNT, FWR_AGNT AS FWDAGNT, (CASE WHEN (B.CNSNE_NM IS NULL OR B.CNSNE_NM = '') THEN ' ' ELSE B.CNSNE_NM END) AS CNSNE, (CASE WHEN (B.SHPR_NM IS NULL OR B.SHPR_NM = '') THEN ' ' ELSE B.SHPR_NM END) AS SHPR, CNSNE AS CNSNE, SHPR AS SHPR, SHIPG_NOTE_NO AS SHIPGNOTENO, BL_NO AS BLNO FROM TMT_CG_MST B WHERE 1 = 1
AND VSL_CALL_ID = #{vslCallId}
AND SHIPG_NOTE_NO = #{shipgNoteNo}
AND BL_NO = #{blNo}

WITH RecursiveCTE AS ( SELECT curr, prev, VSL_CALL_ID, CAST(LOC_ID AS VARCHAR(MAX)) AS LOC_ID_Path FROM ( SELECT INV.VSL_CALL_ID AS VSL_CALL_ID, INV.LOC_ID AS LOC_ID, ROW_NUMBER() OVER (PARTITION BY INV.VSL_CALL_ID ORDER BY INV.LOC_ID) AS curr, ROW_NUMBER() OVER (PARTITION BY INV.VSL_CALL_ID ORDER BY INV.LOC_ID) - 1 AS prev FROM TMT_INV_LOC INV, TMT_GR GR WHERE INV.CG_NO = GR.GR_NO AND INV.VSL_CALL_ID = GR.VSL_CALL_ID
AND GR.SHIPG_NOTE_NO = #{shipgNoteNo}
AND INV.CG_NO = #{grNo}
) WH WHERE curr = 1
UNION ALL
SELECT T.curr, T.prev, T.VSL_CALL_ID, R.LOC_ID_Path + ',' + CAST(T.LOC_ID AS VARCHAR(MAX)) AS LOC_ID_Path FROM RecursiveCTE R JOIN ( SELECT INV.VSL_CALL_ID AS VSL_CALL_ID, INV.LOC_ID AS LOC_ID, ROW_NUMBER() OVER (PARTITION BY INV.VSL_CALL_ID ORDER BY INV.LOC_ID) AS curr, ROW_NUMBER() OVER (PARTITION BY INV.VSL_CALL_ID ORDER BY INV.LOC_ID) - 1 AS prev FROM TMT_INV_LOC INV, TMT_GR GR WHERE INV.CG_NO = GR.GR_NO AND INV.VSL_CALL_ID = GR.VSL_CALL_ID
AND GR.SHIPG_NOTE_NO = #{shipgNoteNo}
AND INV.CG_NO = #{grNo}
) T ON R.curr = T.prev AND R.VSL_CALL_ID = T.VSL_CALL_ID )
SELECT /*spaceMovementPlan.selectSpaceMovementSnInfo*/ A.VSL_CALL_ID AS VSLCALLID, A.CATG_CD AS CATGCD, A.CATG_CD AS OPECLASSCD, (CASE WHEN (A.IMDG IS NULL OR A.IMDG = '') THEN 'N' ELSE 'Y' END) AS DGYN, A.SHIPG_AGNCY AS SHIPGAGNT, A.FWRD AS FWDAGNT, (SELECT TOP 1 E.S_CD_NM FROM TMT_CD_MSTD E WHERE E.L_CD = 'MT' AND E.M_CD = 'CGTP' AND E.S_CD =A.CG_TP_CD ) AS CGTYPE, A.SHIPG_NOTE_NO AS SHIPGNOTENO
,F.WGT AS REQWGT ,F.MSRMT AS REQMSRMT ,F.PKG_QTY AS REQQTY ,E.LOC_ID AS PREVLOCID
, CASE WHEN (D.GR_NO IS NOT NULL AND D.GR_NO <> '') THEN D.WGT ELSE A.CG_WGT END AS REQWGT , CASE WHEN (D.GR_NO IS NOT NULL AND D.GR_NO <> '') THEN D.MSRMT ELSE A.CG_MSRMT END AS REQMSRMT , CASE WHEN (D.GR_NO IS NOT NULL AND D.GR_NO <> '') THEN D.PKG_QTY ELSE A.PKG_QTY END AS REQMSRMT
,A.CG_WGT AS REQWGT ,A.CG_MSRMT AS REQMSRMT ,A.PKG_QTY AS REQQTY
,'' AS PREVLOCID
,B.BL_NO AS BLNO, D.GR_NO AS GRNO, A.CNSNE_NM AS CNSNE, A.SHPR_NM AS SHPR, ISNULL( DBO.F_CM_CODE_NM('MT', 'PKGTP', ISNULL(D.PKG_TP_CD, A.PKG_TP_CD)), DBO.F_CM_CODE_NM('FZ', 'PKGTP', ISNULL(D.PKG_TP_CD, A.PKG_TP_CD)) ) AS CGPKGTYPE, A.EST_ARRV_DT AS ETA, (CASE WHEN (A.CNSNE_NM IS NULL OR A.CNSNE_NM = '') AND (A.SHPR_NM IS NULL OR A.SHPR_NM = '') THEN ' ' WHEN (A.CNSNE_NM IS NULL OR A.CNSNE_NM = '') THEN A.SHPR_NM WHEN (A.SHPR_NM IS NULL OR A.SHPR_NM = '') THEN A.CNSNE_NM ELSE A.SHPR_NM + '/ ' + A.CNSNE_NM END) AS cngShp FROM TMT_SHIPG_NOTE A LEFT OUTER JOIN TMT_BL B ON A.BL_NO = B.BL_NO LEFT OUTER JOIN TMT_VSL_SCH C ON A.VSL_CALL_ID = C.VSL_CALL_ID LEFT OUTER JOIN TMT_GR D ON A.VSL_CALL_ID = D.VSL_CALL_ID AND A.SHIPG_NOTE_NO = D.SHIPG_NOTE_NO
,( SELECT STUFF((SELECT ',' + CAST(LOC_ID AS VARCHAR(MAX)) FROM TMT_INV_LOC WH FOR XML PATH(''), TYPE).value('.', 'VARCHAR(MAX)'), 1, 1, '') AS LOC_ID, WH.VSL_CALL_ID FROM RecursiveCTE WH GROUP BY WH.VSL_CALL_ID ) E ,( SELECT CG.VSL_CALL_ID AS VSL_CALL_ID, SUM(LOC.WGT) AS WGT, --NO COLUMN WGT IN TABLE TMT_INV_LOC SUM(LOC.MSRMT) AS MSRMT, --NO COLUMN MSRMT IN TABLE TMT_INV_LOC SUM(LOC.PKG_QTY) AS PKG_QTY FROM TMT_CG_MST CG, TMT_INV_LOC LOC WHERE CG.VSL_CALL_ID = LOC.VSL_CALL_ID AND CG.CG_NO = LOC.CG_NO
AND CG.SHIPG_NOTE_NO = #{shipgNoteNo}
AND CG.CG_NO = #{grNo}
GROUP BY CG.VSL_CALL_ID ) F
WHERE 1 = 1
AND A.VSL_CALL_ID = E.VSL_CALL_ID AND A.VSL_CALL_ID = F.VSL_CALL_ID
AND A.VSL_CALL_ID = #{vslCallId}
AND A.SHIPG_NOTE_NO = #{shipgNoteNo}
AND B.BL_NO = #{blNo}
AND D.GR_NO = #{grNo}

  		SELECT /*spaceMovementPlan.selectReqSeq*/
  			SEQ 						AS SEQ
  		FROM
			TMT_SPC_REQ
  		WHERE
			VSL_CALL_ID 				= #{vslCallId}


				AND BL_NO 				= #{blNo}


				AND SHIPG_NOTE_NO 		= #{shipgNoteNo}

mapper.delete:
DELETE /*spaceMovementPlan.deleteSpaceMovementPlanItems*/
FROM
	TMT_SPC_REQ
WHERE
	REQ_NO 				= #{reqNo}

	AND SEQ 			= #{seq}


	AND	STAT_CD 		= #{statCd}

DELETE /*spaceMovementPlan.deleteSpcMovPlanItems*/
FROM
	TMT_SPC_PLAN
WHERE
	VSL_CALL_ID 				= #{vslCallId}

	AND REQ_SEQ 				= #{reqSeq}
